css:
@-moz-document url('https://www.opentimeclock.com/free.html') {
  body {
    background-color: #1f1b24 !important;
  }

  #divHeader + div {
    text-align: center !important;
  }

  #divHeader + div #lblCompanyName {
    color: #ffb650 !important;
    font-weight: bold;
    text-decoration: underline;
    opacity: 1;
    -webkit-transition: opacity 0.3s ease;
    transition: opacity 0.3s ease;
  }

  #divHeader + div #lblCompanyName:hover,
  #divHeader + div #lblCompanyName:active,
  #divHeader + div #lblCompanyName:focus {
    opacity: 0.7;
  }

  #lblFullName {
    color: #ffffff !important;
    margin-bottom: 15px !important;
  }

  #divShowClock {
    margin-top: -515px !important;
  }

  #divShowClock span {
    color: #ffffff;
  }

  .input-lg {
    border-radius: 0px;
  }

  #lblRememberPassword {
    color: #aaaaaa;
    text-transform: capitalize;
  }

  #lblRememberPassword + a {
    text-transform: capitalize;
    color: #ffb650;
    text-decoration: underline;
    margin-left: 50px !important;
    opacity: 1;
    -webkit-transition: opacity 0.3s ease;
    transition: opacity 0.3s ease;
  }

  #lblRememberPassword + a:hover,
  #lblRememberPassword + a:active,
  #lblRememberPassword + a:focus {
    opacity: 0.7;
  }

  #lnkFullScreen {
    text-transform: capitalize;
    color: #ffb650;
    text-decoration: underline;
    opacity: 1;
    -webkit-transition: opacity 0.3s ease;
    transition: opacity 0.3s ease;
  }

  #lnkFullScreen:hover,
  #lnkFullScreen:active,
  #lnkFullScreen:focus {
    opacity: 0.7;
  }

  .btn-lg {
    border-radius: 0px;
  }

  .btn-lg#btnLogin {
    text-transform: uppercase;
  }

  #lblCurrentTime + div + div + div + div {
    background-color: transparent !important;
  }

  #lblCurrentTime + div + div + div + div span {
    color: #aaaaaa;
  }

  #lblCurrentTime + div + div + div + div span#lblGps {
    display: block;
    margin-top: 40px !important;
  }

  #lblCurrentTime + div + div + div + div span#lblIp,
  #lblCurrentTime + div + div + div + div span#lblClockId {
    display: inline-block;
  }

  #lblCurrentTime + div + div + div + div span#lblShowCompanyName {
    margin-left: 5px !important;
  }

  #lblCurrentTime + div + div + div + div span#lblShowCompanyName + span {
    display: block;
  }

  #lblCurrentTime + div + div + div + div span#lblShowCompanyName + span a {
    color: #ffb650;
    text-decoration: underline;
    text-transform: capitalize;
    display: inline-block;
    margin-top: 10px;
    opacity: 1;
    -webkit-transition: opacity 0.3s ease;
    transition: opacity 0.3s ease;
  }

  #lblCurrentTime + div + div + div + div span#lblShowCompanyName + span a:hover,
  #lblCurrentTime + div + div + div + div span#lblShowCompanyName + span a:active,
  #lblCurrentTime + div + div + div + div span#lblShowCompanyName + span a:focus {
    opacity: 0.7;
  }

  #lblCurrentTime + div + div + div + div #lnkForgot {
    color: #ffb650;
    text-decoration: underline;
    text-transform: capitalize;
    display: inline-block;
    margin-left: 5px !important;
    opacity: 1;
    -webkit-transition: opacity 0.3s ease;
    transition: opacity 0.3s ease;
  }

  #lblCurrentTime + div + div + div + div #lnkForgot:hover,
  #lblCurrentTime + div + div + div + div #lnkForgot:active,
  #lblCurrentTime + div + div + div + div #lnkForgot:focus {
    opacity: 0.7;
  }

  #lnkCreateNewCompany {
    margin-top: 20px;
    text-transform: capitalize;
    display: inline-block;
    color: #ffb650 !important;
    text-decoration: underline;
    margin-left: 0px !important;
    opacity: 1;
    -webkit-transition: opacity 0.3s ease;
    transition: opacity 0.3s ease;
  }

  #lnkCreateNewCompany:hover,
  #lnkCreateNewCompany:active,
  #lnkCreateNewCompany:focus {
    opacity: 0.7;
  }

  #divHeader {
    background-color: transparent !important;
    width: auto !important;
    height: auto !important;
    padding: 0px !important;
  }

  #divHeader #divHeadTitle {
    background-color: #121212;
    padding: 5px;
    margin-bottom: 30px;
  }

  #divHeader #divHeadTitle a {
    text-transform: capitalize;
    display: inline-block;
    color: #ffb650 !important;
    text-decoration: underline;
    opacity: 1;
    -webkit-transition: opacity 0.3s ease;
    transition: opacity 0.3s ease;
  }

  #divHeader #divHeadTitle a:hover,
  #divHeader #divHeadTitle a:active,
  #divHeader #divHeadTitle a:focus {
    opacity: 0.7;
  }

  #divFooter {
    background-color: #121212 !important;
    border: none !important;
    padding: 5px;
  }

  #divFooter #divFoodTitle a {
    text-transform: capitalize;
    display: inline-block;
    color: #ffb650 !important;
    text-decoration: underline;
    opacity: 1;
    -webkit-transition: opacity 0.3s ease;
    transition: opacity 0.3s ease;
  }

  #divFooter #divFoodTitle a:hover,
  #divFooter #divFoodTitle a:active,
  #divFooter #divFoodTitle a:focus {
    opacity: 0.7;
  }
}
