css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("photo-forum.net") {

/*razmer na 1024*/
div#wrapper > table > tbody > tr > td {width: 597px !important;}
.forum_frame, .PhorumListTable, .forum_frame_title, .forum_bottom_middle, .forum_frame_footer {width: 592px !important;}
.forum_frame_middle {width: 563px !important;}
div.forum_frame > table, .forum_bottom_middle, div.forum_frame > table > tbody > tr > td > table.PhorumListTable {width: 584px !important;}
.footer_forum_middle {width: 566px !important;}
#wrapper, #content {width: 1000px !important;}
#header {width: 996px !important;}
td.PhorumListRow[style="height: 40px;"], td.PhorumListRowOdd[style="height: 40px;"] {width: 700px !important;}
#page_2_columns {width: 802px !important;}

/*------purva stranica*/
#page, #first_column, #second_column, .second_menu {width: 576px !important;}
#page {margin: 0 8px 0 8px !important;}
.page_menu_header, #page_menu_content {width: 576px !important;}
#page_menu_header_content {width: 529px !important;}
.page_menu_content_bg, #page_menu_content_top, #page_menu_content_bottom {width: 576px !important; background-repeat: repeat !important; background-position: left top !important;}
.list_element_type2, .list_content_text, .list_element {width: 540px !important;}

.second_menu_header_content {width: 552px !important;}
.second_menu_content {width: 566px !important;}
/*------centrirane na menu*/
.menu_texts {position: relative !important; width: 860px !important; text-align: center !important; left: 50% !important; margin-left: -430px !important; top: -28px !important;}
.menu {text-align: center !important; width: 996px !important;}
#top_navi_back_center {width: 762px !important;}
.menu_texts ul, .menu_texts ul li, .menu_item, .menu_item a {display:inline !important; float: none !important;}
.menu_item a {padding: 8px 20px 8px 25px !important;}
div.menu_item_last {display: none !important;}
.menu_texts ul {padding: 2px 10px 5px 0px !important; background: url(http://img.photo-forum.net/site_design/top_menu_separator.jpg); background-repeat: no-repeat; background-position: right center;}

#user_info, #user_top_info {width: 567px !important;}
.ekipirovka_top, .ekipirovka_container, .ekipirovka_bottom, .ekipirovka_bg {width: 797px !important;}
#choice_of_author_userprofile {width: 792px !important;}
#second_cell_user_info > div {font-size: 9px !important;}
 #ekipirovka_second_column {width: 472px !important;}
#thumbnail_page, #thumbnail_title, .flow_text {width: 792px !important;}

#wrapper{background:url(http://img7.imageshack.us/img7/4277/bgcontentp1024.gif) repeat-y !important;}
#wrapper:first-child > table {width: 672px !important;}


/*skrivane ikoni vidove temi*/
img[src="images/nikon.gif"], img[src="images/canon.gif"], img[src="images/panasonicolympus.gif"], img[src="images/pentax.gif"], img[src="images/sony.gif"], img[src="http://img.photo-forum.net/site_design/obshti.gif"], img[src="http://img.photo-forum.net/site_design/fotografia.gif"], img[src="http://img.photo-forum.net/site_design/tehnika.gif"], img[src="http://img.photo-forum.net/site_design/novaci.gif"], img[src="http://img.photo-forum.net/site_design/izlojbi.gif"] {display:none !important;}


/*skrivane reklami*/
object {display: none !important;}
iframe {display: none !important;}
div#banner_top {background-image: url(http://img529.imageshack.us/img529/4623/banere.gif) !important;}
div.advertising_text {background-image: none !important;}

/*smqna na logoto*/
img[src="http://img.photo-forum.net/site_design/logo.gif"]{display:none !important;}
div#header_img_left {background-image: url(http://img507.imageshack.us/img507/5166/leftjpgheader.gif) !important;}

.PhorumListRow {background-color: #eee !important;}
.PhorumSubject, .PhorumListRow {color: #035 !important;}

.link_12_black {color: #023 !important; font-size: 11px !important;}
.PhorumMessageHeader {color: #123 !important; font-size: 12px !important;}
.PhorumTxtStyle {color: #023 !important; font-size: 11px !important; font-weight: normal !important;}
.PhorumTxtStyle[style="cursor: default;"]:hover {color: #023 !important; font-size: 11px !important; font-weight: bold !important;}
}
