    justify-content: flex-start;
    position: relative;
    top: 0px;
    left: 0px;
    height: 22px;
    display: inline-block;
  }

  .charsheet .sheet-spell-expansion label
  {
    padding-right: 1px;
    display: inline-block;
  }

