body.boxed {
    background: #000000;
}

.listing-grid .excerpt {
    background-color: #000;
}

.excerpt > p:first-child {
    color: white;
}

.excerpt > p:first-child {
    color: white;
}

.column, .ts-row [class*=col-] {
    background-color: black;
}

.main-head header {
    background: #000;
}

.boxed .main-wrap {
    background: #000;
}

.post-content {
    color: white;
}

.post-content h1, .post-content h2, .post-content h3, .post-content h4, .post-content h5, .post-content h6 {
    color: white;
}

tr:nth-child(odd) td {
    background: #000000;
}

.post-header .post-title {
    color: white;
}

#wpcomm .wpdiscuz-front-actions {
    background: #000000;
}

#wpcomm .wc_comm_form.wc_main_comm_form .wc-field-textarea .wpdiscuz-textarea-wrap textarea {
    background-color: black;
}

#wpcomm .wc-comment-right .wc-comment-text p {
    background-color: #000;
}

#wpcomm .wc-comment-header {
    background-color: #000;
}

#wpcomm .wc-comment-footer {
    color: #fff;
    background-color: #000;
}

#wpcomm .wc-reply .wc-comment-label span {
    color: white;
    background-color: #000;
}

#wpcomm .wc-comment-right .wc-comment-text {
    background-color: #000;
    color: white;
}

#wpcomm .wc-reply .wc-comment-right {
    background: #000000;
}

#wpcomm .wc-comment .wc-comment-right {
    background: #000000;
}

#wpcomm .wc-comment .wc-comment-left .wc-comment-label {
    background: #000;
}

tr:nth-child(odd) td {
    background: #000000;
}

div#ez-toc-container p.ez-toc-title {
    background-color: black;
    color: white;
}

#ez-toc-container.counter-flat ul, #ez-toc-container.counter-hierarchy ul, .ez-toc-widget-container.counter-flat ul, .ez-toc-widget-container.counter-hierarchy ul {
    background-color: black;
}

.post-content, th, td {
    background: #000;
}

.post-content a {
    color: #8dd8ff !important;
}

a, .posts-grid .post-link, .top-bar-content a, .post-content a {
    color: #8dd8ff;
}

.navigation .menu>li>a, .navigation.inline .menu>li>a {
    color: black !important;
}


#review-statistics .review-wrap-up .review-wu-right ul li, #review-statistics .review-wu-bars h3, .review-wu-bars span, #review-statistics .review-wrap-up .cwpr-review-top .cwp-item-category a {
    color: #ffffff;
}

.archive-heading .prefix {
    color: #ffffff;
}

#wpcomm a {
    color: #8dd8ff;
}

.main-pagination .page-numbers, .main-pagination a {
    color: #b7e7ff;
}

.main-pagination .page-numbers, .main-pagination a, .post-pagination > .page-link {
    color: #8cd7d6;
    background: #000000;
}

.search-field {
    color: white;
}

input, textarea, select {
   background: #000000;
}

.fa {
    color: white;
    background: black;
}

button {
    background-color: #000000;
    background-image: linear-gradient(to bottom, #000000, #000000);
}

.textwidget p{
    color: white;
}

.notification-dialog {
    background: #3f4850;
}

.media-menu .active, .media-menu .active:hover {
    color: #ffffff;
}

.media-menu .media-menu-item {
    color: #7accf4;
}

.attachments-browser .instructions {
    color: #ccc;
}

.ui-accordion .ui-accordion-header {
    background: black;
}

label.wppr-upsell-label {
    background: #2b4a61;
}

.wppr-review-options-list label {
    background: #706c6c;
}
}
