css:
@-moz-document domain("bilibili.com") {
/* 背景 */
html,
.international-footer {
    background-color: unset !important;
}
body:before {
    content: "";
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    z-index: -100;
    background-image: url(https://images3.alphacoders.com/955/955224.png);
    background-position: center top;
    background-size: cover;
    background-attachment: fixed;
    opacity: 0.8;
}
.special-cover,
.room-bg {
    display: none;
}
/* 首页顶栏 */
.bili-banner {
    min-height: 0 !important;
    height: 50px !important;
    background: var(--theme-color) !important;
}
.custom-navbar.transparent::before {
    height: 50px !important;
    background: none !important;
}
.animated-banner {
    display: none;
}
/* 在线列表 */
#reportFirst2 {
    display: none;
}
/* 评论区等 */
.bb-comment,
.comment-bilibili-fold,
#reco_list,
.rank-item {
    background: rgba(256, 256, 256, 0.6);
}
/* 弹幕字体 */
.b-danmaku {
    font-family: '华康少女字体' !important;
}
/* 播放互动 */
.bilibili-player-show,
.bilibili-player-video-popup-vote {
    display: none;
}
}
