css:
@-moz-document domain("keylol.com") {
/* 放大表情 */
#fastpostsmiliesdiv_table img,
#postsmiliesdiv_data img {
    width: 80px !important;
    height: unset !important;
}
/* 背景 */
body {
    background: url(https://pic.imgdb.cn/item/5edb102bc2a9a83be5da660e.jpg);
    background-position: center top;
    background-size: cover;
    background-attachment: fixed;
}
/* 去背景色 */
.subforum_left_title {
    background: none;
}
/* 首页 */
.frame-1-1-l {
    width: 480px;
}
.frame-1-1-r {
    padding-left: 490px;
}
#frameCX66dD_left img,
#frameCX66dD_left .slideshow li {
    width: auto!important;
    height: 468px!important;
}
#frameCX66dD,
.frame-tab .xl {
    height: 468px;
}
.frame-tab .xl:hover {
    padding-right: 11px;
}
/* 板块导航 */
.doc_header,
.bml {
    display: none;
}
}
