css:
@-moz-document domain("duckduckgo.com") {
	

body {
	background: /*[[background]]*/ !important;
	margin-left: -10px !important;
	margin-right: 0px !important;
	color: /*[[foreground]]*/ !important;
}
.results--main .result:not(.result--ad).highlight{
	border: 1px solid /*[[outline]]*/;
	background: rgba(0, 0, 0, 0);
	border-radius: 0px !important;
}
.module__content {
	background: /*[[background]]*/ !important;
}
.module__toggle{
	background: /*[[background]]*/ !important;
	box-shadow: none !important;
	border-top: none !important;
}

/* expand arrow */
.module__toggle__chevron {
       top:-3px !important;
 }


.modal--popout .modal__box,
.modal__box,
.sidebar-modules .module,
.results--main .result:not(.result--ad).highlight,
.module .module--carousel__item,
.user-loc-cta,
.address-detail,
.modal--dropdown.modal--popout .modal__box,
.tile--info,
.place-detail,
.badge-link,
.is-atb-fullbleed .content-info__item,
.atb-banner__bottom,
.is-mobile .results_links_deep,
.is-mobile .results_links,
.is-mobile .result--news,
.most-visited__info-tt {
  border: 1px solid /*[[outline]]*/ !important;
  background: /*[[background]]*/ !important;
  border-radius:0px !important;
}


#links_wrapper{
	background-color: /*[[background]]*/ !important
}
.search-filters-wrap::after, .search-filters-wrap::before, .module__toggle--more::after, .module__toggle--more::before, .metabar__dropdowns-wrap::before, .metabar__dropdowns-wrap::after {
	background-image: none;
}
#header{
	background-color: /*[[background]]*/ !important;
	margin-top: -20px;
	padding-right: 500px;
}
/* cheetsheet */
#zci-cheat_sheets > div:nth-child(1) {
	background-color: /*[[background]]*/ !important;
}
.zci.is-active {
	 border-bottom:0px solid /*[[outline]]*/;
	 padding: 0em;
	 margin-top:10px;
	 visibility:visible;
	 overflow:visible;
	 max-height:none;
	 display:block
}
/* videos */
.tile, .tile-wrap {
	border: 0px solid /*[[foreground]]*/;
	border-radius: 0px;
 	background: /*[[background]]*/!important;
	box-shadow: 0px 0px 1px 0px 
}
	
.highlight.tile {
	border: 2px solid /*[[outline]]*/;
}

div.detail__pane:nth-child(2) {
	 line-height: 380px;
	 width: 1240px;
	 height: 380px;
	 padding: 16px;
	 transform: translateX(0px);
	 display: block;
	 background: /*[[background]]*/;
}
.metabar{
	background: /*[[background]]*/ !important;
	color: /*[[foreground]]*/ !important;
}
/* wrapper */
.site-wrapper{
	padding-top: 10px;
	background-color: /*[[background]]*/ !important;
}
/* settings, feedback, share, footbar, log */
.header--aside, .dropdown--settings > a:nth-child(1), css.serp__top-right,
.serp__bottom-right, .footer, .header__logo {
	display: none !important;
}
	

	
/* Search bar */
.search--home, .search--header{
	background-color: /*[[background]]*/ !important;
 	border-color: /*[[outline]]*/ !important;
	border-radius: 0px;
	box-shadow: none;
	margin-top: 10px;
	width: auto !important;
}
/* search button */
.search--header.has-text.search--hover .search__button:hover, .search--header.has-text.search--hover .search__button, .search--hover .search__button{
	background-color:transparent;
	color:/*[[outline]]*/;
}

/* minimized */
.is-mobile .results_links_deep,
.is-mobile .results_links,
.is-mobile .result--news {
 box-shadow:0 2px 3px rgba(0,0,0,0.06);
 border:0px solid rgba(150,150,150,0.3);
 border-bottom-color:rgba(125,125,125,0.3);
 margin:0 0 7px 0
}

/* feedback promt */
.feedback-prompt {
       	display: none;
}

.is-module .module--images {
	border-radius:0px !important;
}

.header__logo-wrap {
       	display: none;
}
.header-wrap,
.vertical--map--sidebar-left .has-requery .vertical--map__sidebar__header,
.map-requery-mobile,
.footer,
.footer--mobile {
 background-color: /*[[background]]*/ !important;
}
}
