css:
@-moz-document domain("physicsforums.com") {

.p-body-pageContent, p-body-sidebar, #footer, .p-pageWrapper, .structItem {
 	background-color:/*[[background]]*/ !important;
 	padding:0px;
 	border-radius:0px;
 	border:0px;
	color: /*[[foreground]]*/ !important
}

/* title bar */
.p-nav {
 color:#4c4c4c !important;
 background: /*[[background]]*/ !important;
 box-shadow:none !important;
 border-bottom:1px solid /*[[outline]]*/;
}

.p-nav-list .p-navEl {
 color:#4c4c4c;
}

/* selecting hover */
	.p-nav-list .p-navEl.is-selected {
 color:#5284c1;
 background:/*[[background]]*/ !important;
}

.p-nav-inner{
 	background: none !important;
}

.block--category .block-header {
 border-bottom:0px solid /*[[outline]]*/ !important;
}

/* hover background */
.p-nav-list .p-navEl:not(.is-selected):not(.is-menuOpen):hover,
.p-nav-list .p-navEl:not(.is-selected):not(.is-menuOpen) .p-navEl-link:hover,
.p-nav-list .p-navEl:not(.is-selected):not(.is-menuOpen) .p-navEl-splitTrigger:hover {
 text-decoration:none;
 background:rgba(34,34,34,0.06);
 background-color: /*[[outline]]*/ !important;
	color: /*[[foreground]]*/ !important;
}

/* topics borders */
.block--category .block-body.nodedual .node:nth-child(odd) {
 border-right:0px solid /*[[outline]]*/;
}

.block--category .block-container .block-body	{
 border:1px solid /*[[outline]]*/  !important;
 border-radius: 0px !important;
}

/* hover menu */
.menu-content{
	background: /*[[outline]]*/ !important;
}
.menu-linkRow.is-selected,
.menu-linkRow:hover,
.menu-linkRow:focus {
 color:/*[[foreground]]*/ !important;
 text-decoration:none;
 background:none !important;
 border-left-color:/*[[foreground]]*/ !important;
}

/* box borders */
.block--category .block-container .block-body {
border:0px solid #e6e6e6;
box-shadow: none !important;
}

div.p-breadcrumbs--parent:nth-child(1) > ul:nth-child(1), .node > div:nth-child(1) > div:nth-child(3) > div , .p-breadcrumbs{
 background: /*[[background]]*/ !important;
}
.block-container, .block-header {
 color:/*[[foreground]]*/ !important;
 background:/*[[background]]*/ !important;
 border:0px solid #eee;
 border-radius:0px;
}

div.block:nth-child(7) > div:nth-child(2) > div:nth-child(1) > h2:nth-child(1) {
 border: 0px;
}
/* boottom support menu */
.p-footer-default, .p-footer-copyright, .block-footer{
	background: /*[[background]]*/ !important;
	border: 0px solid /*[[outline]]*/;
	border-radius: 0px;
	color: /*[[outline]]*/;
}

/* posts */
.block--messages article.message, .block-messages .messages, .block-messages .block-row, .message-responseRow {
	background: /*[[background]]*/ !important;
	color: /*[[foreground]]*/ !important;
	border-radius:0px !important;
}

/* quotes */
.bbCodeBlock{
	background: /*[[background]]*/ !important;
	border: 0px !important;
	border-left: 2px solid !important;
	border-radius: 0px;
	margin-left: 10px
}
.bbCodeBlock-title{
	background: /*[[background]]*/ !important;
	color: /*[[foreground]]*/ !important;
}
.XenBase .block--messages .message .message-content .bbCodeBlock-title a,
.XenBase .block--messages .message .message-content .bbCodeBlock-title a:hover {
 color:#1c58a5;
}
/* user info */
.message-cell.message-cell--user,
.message-cell.message-cell--action {
 position:relative;
 background: /*[[background]]*/ !important;
 border-right:1px solid #dfdfdf;
 min-width:0
}
/* insane arrows */
.block--messages .message:not(.message--forceColumns) .message-userArrow {
	display: none;
}

/* margin b/w messages */
.block--messages article.message {
 background:#F6F6F6;
 border:1px solid /*[[outline]]*/;
 padding:0px;
 margin-top:50px !important;
 
}

/* date, time header */
.message-header .message-attribution {
	border: 0px;
  	background:/*[[outline]]*/ !important;
}

/* reaction bar */
.reactionsBar {
 background:/*[[background]]*/ !important;
 border:0px;
 padding:10px;
 font-size:13px;
 margin-top:10px
}

/* login promt */
.overlay {
 position:relative;
 margin:20px auto 10px;
 background:/*[[background]]*/ !important;
 color:/*[[outline]]*/ !important;
 border:1px solid #eee;
 border-radius: 0px !important;
}

.overlay-title, .formSubmitRow-bar, .formRow > dt, .input:focus .input.is-focused, .input, .inputGroup.inputGroup--joined .inputGroup-text {
 background:/*[[background]]*/ !important;
 color: /*[[foreground]]*/ !important;
 border: 0px;
 border-radius: 0px !important;
}

.input:focus .input.is-focused, .input, .inputGroup.inputGroup--joined .inputGroup-text   {
	background: /*[[background]]*/ !important;
	color: /*[[foreground]]*/ !important;
	border-radius: 0px;
	border: 1px solid /*[[outline]]*/ !important
	}	

/* topics pannel */
.tabs--standalone {
 padding:0;
 border:0px solid /*[[outline]]*/;
 color:/*[[foreground]]*/;
 background:/*[[background]]*/;
}
.pageNav-page, .pageNav-jump {
 background:/*[[background]]*/ !important;
 color:/*[[outline]]*/;
 border:0px solid #eee;
}
/* filter, watching menu */
div.block-row {
 background: /*[[background]]*/ !important;
}
.block-filterBar {
 background:none !important;
 border-bottom:0px solid #d2d2d2;
}

/* side bar */
.XenBase .p-body-sidebar .block-minorHeader {
 color:#616161;
 border-bottom:0px solid #dfdfdf;
}
/* no reply thread */
.js-threadList .replyCountZero {
background:none !important;
border-left: 3px solid /*[[foreground]]*/ !important;
}
/* pinned posts seperator */
.structItemContainer-group.structItemContainer-group--sticky:after {
 background:/*[[foreground]]*/ !important;
 height:1px;
 margin-top: 10px !important;
 margin-bottom: 10px !important; 
}
.button,
a.button {
 border: none !important;
 background: none !important;
}

/* reply box */
.fr-box.fr-basic .fr-wrapper  {
 background:/*[[background]]*/ !important;
 color: /*[[foreground]]*/ !important;
 border:1px solid /*[[outline]]*/ !important;
 border-radius: 0px !important;
}
.fr-toolbar {
 color:/*[[foreground]]*/ !important; 
 background:/*[[background]]*/ !important; 
 padding:0 1px;
 border-radius:0px !important;
 -moz-border-radius:0px !important;
 border:1px solid /*[[outline]]*/ !important;
 line-height:1.2
}
	
.fr-toolbar .fr-command.fr-btn, .fr-popup .fr-command.fr-btn, .fr-separator, .button {
color: /*[[outline]]*/ !important;}

.fr-separator {
 background:/*[[outline]]*/ !important;}

/* new thread */	
.fr-box.fr-basic .fr-element, .input input {
 color:/*[[foreground]]*/ !important;}

.attachUploadList, .attachment {
 background:none !important;
 border:none !important;
}

/* similar thread */
.block-container.block--similarContents {
border-radius: 0px !important;
}

/* MY PF menu */
.menu-tabHeader {
color: /*[[foreground]]*/ !important;
background: /*[[background]]*/ !important;}
	
	
.menu-row.menu-row--alt {
 color:/*[[foreground]]*/ !important;
 background:none !important;
}
.menu-tabHeader .tabs-tab.is-active {
 color:/*[[outline]]*/;
 border-top: 2px solid /*[[outline]]*/ !important;
}
.menu-content {
 border-radius:0px;
 border:0px solid /*[[outline]]*/;
}
.menu-footer {
 border: none !important;
 background: none !important;
}
textarea.input:nth-child(2) {
 border: 1px solid /*[[background]]*/  !important;
 background: none !important;
}

}
