css:
@-moz-document domain("wikipedia.org"), domain("wikimedia.org") {
/* -=- Variables -=- */
:root {
    --background: #181818;
    --background-2: #202020;
    --background-3: #282828;
    --background-4: #303030;
    --background-5: #383838;
    --background-black: black;
    --background-alert: #0c1827;
    --foreground: #ccc;
    --foreground-2: white;
    --foreground-footer: #bbb;
    --foreground-disabled: #828282;
    --foreground-code: #39892f;
    --link: #608dff;
    --link-invalid: #ff6961;
    --link-hover: #637ab5;
    --link-invalid-hover: #b66763;
    --border: #696969;
    --border-input: #898989;

    --main: #347bff;
    --main-hover: #669cff;
    --main-light: #f0f8ff;
    --error: #5c0000;
    --warn: #664b00;

    --diff-del: #551611;
    --diff-ins: #184e42;
    --diff-old: #775900;
    --diff-new: #133656;
}

/* -=- Main Styling -=- */
body,
.mw-body {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
    color: var(--foreground);
    background-color: var(--background) !important;
}

a,
.tocnumber {
    color: var(--link) !important;
    text-decoration: none;
}

a:hover {
    color: var(--link-hover) !important;
    text-decoration: underline;
}

.new,
.new:link,
.new:visited {
    color: var(--link-invalid) !important;
}

.new:hover,
.new:visited:hover {
    color: var(--link-invalid-hover) !important;
}

.b,
div,
th,
#pagehistory li.selected,
[style*="color: black"] {
    color: var(--foreground-2) !important;
}

h1 {
    border: 0px !important;
    font-family: 'Arial' !important;
    font-weight: 700 !important;
    font-size: 36px !important;
    color: var(--foreground-2) !important;
}

h2 {
    border: 0px !important;
    font-family: 'Arial' !important;
    font-weight: 400 !important;
    font-size: 26px !important;
    color: var(--foreground-2) !important;
}

h3 {
    border: 0px !important;
    font-family: 'Arial' !important;
    font-weight: 400 !important;
    font-size: 16px !important;
    color: var(--foreground-2) !important;
}

h4 {
    color: var(--foreground-2) !important;
}

p {
    font-weight: 300 !important;
    line-height: 1.7 !important;
    color: var(--foreground) !important;
}

[style*="background:#f8f9fa"] {
    background: var(--background-3) !important;
    border: none !important;
}

.reflist,
.refbegin {
    background-color: var(--background-3) !important;
}

.thumbcaption {
    color: var(--foreground-disabled) !important;
}

#content h3 {
    font-size: 20px !important;
}

.portal-column-right span,
.portal-column-left span,
body.ns-100 .mw-body-content h2,
.mw_metadata th,
.tracklist th,
#mp-tfl-h2 {
    color: var(--foreground) !important;
    background-color: var(--background-5) !important;
}

body.ns-100 .mw-body-content th,
body.ns-100 .mw-body-content div,
body.ns-100 #portal td,
.mw_metadata td,
.tracklist td,
.tracklist div {
    color: var(--foreground) !important;
    border-width: 0px !important;
    background-color: var(--background-4) !important;
}

.portal-column-right,
.portal-column-left,
body.ns-100 .mw-body-content .subpages {
    color: var(--foreground) !important;
    background-color: var(--background-2) !important;
    border-width: 0px !important;
}

.mw-highlight {
    background-color: var(--background-2) !important;
}

.mw-highlight .nc,
.mw-highlight .nf {
    color: var(--link);
}

.mw-notification {
    background-color: var(--background-4) !important;
}

.mw-echo-ui-notificationItemWidget {
    background-color: var(--background-2);
}

.mw-echo-ui-notificationItemWidget-unread {
    background-color: var(--background-4);
}

.mw-echo-ui-notificationItemWidget:hover {
    background-color: var(--background-5);
}

.oo-ui-icon-userAvatar,
.mw-ui-icon-userAvatar:before,
.oo-ui-icon-userSpeechBubble,
.oo-ui-icon-settings,
.mw-ui-icon-settings:before,
.oo-ui-indicator-down,
.mw-echo-ui-notificationItemWidget-icon img[src="/static/images/project-logos/notifications/120px-Notification-icon-Wikipedia-logo.svg.png"] {
    filter: invert(100%);
}

.mw-echo-ui-menuItemWidget > .oo-ui-labelElement-label,
.mw-echo-ui-notificationItemWidget-content-actions-timestamp {
    color: var(--foreground);
}

.mw-echo-ui-notificationsInboxWidget-toolbarWrapper {
    background-color: var(--background-2);
}

#Example {
    color: var(--foreground) !important;
}

table,
td,
th {
    border: 0px !important;
    border-collapse: collapse !important;
    padding: 5px !important;
    background-color: var(--background-5) !important;
    /* Colored table cells are replaced with this background-color */
}

ul,
li,
caption {
    background: transparent !important;
    border: 0px !important;
    line-height: 1.8 !important;
    font-size: 14px !important;
    font-weight: 300 !important;
}

li span {
    background: transparent !important;
    border: 0px !important;
    font-size: 14px !important;
}

.referencetooltip,
.rt-tooltip {
    background-color: var(--background-3) !important;
}

.rt-tooltipTail::after {
    background: var(--background-3);
}

dl {
    font-weight: 300 !important;
}

dt {
    font-style: italic;
    font-size: 16px !important;
    margin-top: 10px !important;
}

dd {
    font-size: 15px !important;
}

.warningbox {
    background-color: var(--warn) !important;
}

#p-logo a {
    background-color: var(--background-2) !important;
}

.mw-wiki-logo {
    padding-left: 15px !important;
    padding-top: 10px !important;
    border: none !important;
}

code {
    padding: 3px !important;
    background: transparent !important;
    color: var(--foreground-code) !important;
    border: 1px solid var(--border) !important;
}

.hatnote span {
    color: var(--foreground-footer) !important;
    background-color: var(--background-2) !important;
}

tt {
    font-family: 'Source Code Pro', 'Lucida Sans Unicode', 'Courier New' !important;
    font-size: 16px !important;
}

blockquote {
    border: 0px !important;
    font-style: italic !important;
    font-size: 20px !important;
    margin: 35px 75px !important;
    line-height: 1.5 !important;
    font-weight: 300 !important;
    background: transparent !important;
}

select {
    padding: 0px 10px;
}

.mw-ui-button,
.mw-ui-progressive {
    border: 1px solid var(--border) !important;
    color: var(--main) !important;
    background: var(--background-2) !important;
    text-shadow: 0px 0px 0px;
}

.mw-ui-button.mw-ui-progressive:hover,
.mw-ui-button.mw-ui-progressive:focus,
.mw-ui-button.mw-ui-progressive:active,
.mw-ui-button:hover,
.mw-ui-progressive:hover,
.mw-ui-button:focus,
.mw-ui-progressive:focus {
    box-shadow: 0px 0px 0px !important;
    text-shadow: 0px 0px 0px !important;
    border: 1px solid var(--main) !important;
}

.app-badges .footer-sidebar-content {
    background-color: var(--background-2) !important;
}

.mw-mmv-info-box {
    background-color: var(--background) !important;
}

/* Media Viewer Image Preview */
.mw-mmv-above-fold {
    background-color: var(--background-4) !important;
}

.mw-mmv-image-metadata {
    background-color: var(--background) !important;
}

.mw-mmv-dialog,
.mw-mmv-dialog-down-arrow,
.mw-mmv-download-attribution,
.mw-mmv-download-pane,
.mw-mmv-download-pane :hover {
    background-color: var(--background-4) !important;
}

/* Publishing Changes */
#wpSave,
#wpPreview,
#wpDiff {
    border-radius: 4px !important;
    border: 1px solid var(--main) !important;
    color: var(--main) !important;
    background: var(--background-2) !important;
    text-shadow: 0px 0px 0px;
    padding: 0.5em 1em;
    cursor: pointer;
}

#wpSave:hover,
#wpPreview:hover,
#wpDiff:hover {
    color: var(--main-hover) !important;
}

/* Search */
#simpleSearch {
    position: relative;
    overflow: hidden;
    background: transparent !important;
    border-color: var(--border) !important;
}

#searchInput {
    height: 100%;
    color: var(--foreground) !important;
    background: transparent !important;
}

input#ooui-1.oo-ui-inputWidget-input {
    color: var(--foreground) !important;
    background: transparent !important;
}

#mw-searchoptions,
#searchText {
    color: var(--foreground) !important;
    background-color: var(--background-5) !important;
}

.mw-ui-vform .warning {
    background-color: var(--warn) !important;
}

.mw-ui-vform .error {
    background-color: var(--error) !important;
}

.mw-ui-input {
    border: 1px solid var(--border) !important;
    color: var(--foreground) !important;
    background: var(--background-2) !important;
    font-size: 16px !important;
}

.mw-ui-input:focus {
    box-shadow: 0px 0px 0px !important;
}

.mwe-math-fallback-image-inline {
    filter: invert(100%);
}

.mwe-math-fallback-image-inline img,
.mwe-math-element img,
img[src*="LaTeX"] {
    filter: invert(100%);
}

.mwe-popups,
div.navpopup {
    background-color: var(--background-4) !important;
    font-size: 16px !important;
}

a.mwe-popups-extract::after {
    background: var(--background-4) !important;
}

.mw-headline {
    color: var(--foreground) !important;
}

.mw-content-ltr pre {
    font-family: "Courier New", Lucida Console, sans-serif !important;
    color: var(--link) !important;
}

.suggestions {
    font-weight: 300 !important;
    font-size: 15px !important;
    max-width: 300px !important;
    margin-top: 15px !important;
    border: 0px !important;
    box-shadow: 2px 2px 10px 2px rgba(0, 0, 0, 0.5);
}

.suggestions-results {
    border: 0px !important;
}

.suggestions-result {
    padding: 10px !important;
}

.suggestion-link {
    background-color: var(--background);
}

.suggestion-link:hover,
.suggestion-link:active {
    background-color: var(--background-4);
}

.imeselector,
#searchEngines {
    display: none !important;
}

.mw-search-formheader {
    border: 0px !important;
    background: transparent !important;
}

.bookend {
    background-image: none !important;
}

.link-box em,
.link-box small {
    color: var(--foreground-footer) !important;
}

.search-form fieldset {
    background-color: var(--background-5) !important;
    border-color: var(--border-input) !important;
    border-width: 1px !important;
}

.oo-ui-tagItemWidget.oo-ui-widget-enabled:not(.oo-ui-tagItemWidget-fixed) {
    background-color: var(--background-5) !important;
}

#searchInput,
#searchLanguage,
#langsearch-input {
    background-color: var(--background-2) !important;
    border-color: var(--border-input) !important;
    border-width: 1px !important;
}

.formBtn {
    background-color: var(--background-2) !important;
    border-color: var(--border-input) !important;
    border-width: 1px !important;
    border-radius: 0px !important;
}

.mw-search-result-heading,
.searchresult,
.searchmatch {
    font-size: 14px !important;
    font-weight: 400 !important;
    padding: 3px 0px !important;
}

.searchmatch {
    font-weight: 700 !important;
}

.mw-search-result-data,
.mw-search-createlink {
    display: none;
}

#mw-page-base,
.vectorTabs {
    background: transparent !important;
    border-width: 0px !important;
}

#content {
    color: var(--foreground) !important;
    border: 0px !important;
    margin-left: 215px !important;
}

.portal,
.portal table {
    background: transparent !important;
}

#mw-panel {
    width: 200px !important;
    border-right: 1px solid var(--border);
    background-color: var(--background-2) !important;
    padding-left: 10px !important;
    ;
}

#p-logo {
    left: 0px;
    padding-left: 10px !important;
    background-color: white;
}

#p-logo a {
    width: 11em !important;
    padding-left: 40px;
    background-color: white;
    left: 0px;
    border-right: 1px solid var(--border);
    margin-left: -1em;
}

#p-logo a:hover {
    opacity: 1.0;
}

#mw-head {
    color: var(--foreground) !important;
    background-color: var(--background-3);
    opacity: 1.0;
    box-shadow: 0px 0px 2px var(--background-3);
    transition-property: opacity !important;
    transition-duration: 0.25s !important;
}

#mw-head:hover {
    opacity: 1.0 !important;
}

.uls-settings-trigger {
    display: none !important;
}

#left-navigation {
    margin-left: 233px !important;
}

.wikitable {
    font-weight: 300 !important;
}

.wikitable td,
.wikitable th {
    border: 1px solid var(--border) !important;
}

pre {
    line-height: 1.3 !important;
    font-family: "Courier New", Lucida Console, sans-serif !important;
    border: 0px !important;
    background: 0px !important;
    font-size: 16px !important;
    color: var(--main-light) !important;
}

.mw-code {
    border: 0px !important;
    padding: 17px !important;
    background: 0px !important;
    font-size: 16px !important;
}

#bodyContent tbody,
#mp-left,
.MainPageBG,
#mp-left div,
#mp-right div,
#mp-topbanner,
.list,
.template-documentation,
.collapsible th,
.autocollapse th {
    color: var(--foreground) !important;
    background-color: var(--background-4) !important;
    border-color: var(--border) !important;
}

#js-lang-list-button {
    background-color: var(--background-4) !important;
}

.app-badges {
    background-color: var(--background-2) !important;
}

.wikitable,
.wikitable th,
.wikitable rh,
.plainrowheaders,
.nounderlines,
.wikitable tr,
.wikitable td,
.navbox-title,
.navbox-group,
.navbox-odd,
.navbox-even,
#mp-left h2,
#mp-right h2,
#mp-bottom h2,
.navbox-abovebelow,
.group {
    color: var(--foreground) !important;
    background-color: var(--background-5) !important;
}

body.ns-0 #content tbody code {
    color: var(--foreground-code) !important;
    background-color: var(--background-2) !important;
    border-width: 0px !important;
}

.suggestions-results,
.suggestions-special {
    background-color: var(--background-3) !important;
}

.suggestions-results:hover,
.suggestions-special:hover {
    background-color: var(--background-4) !important;
}

.suggestions-results span,
.special-query {
    color: var(--foreground) !important;
}

.suggestions-result,
special-label {
    color: var(--foreground-footer) !important;
}

.highlight {
    color: var(--background-5) !important;
}

.mw-search-profile-tabs {
    color: var(--foreground) !important;
    background-color: var(--background-4) !important;
    border-color: var(--border) !important;
}

.wikitable tr,
.wikitable td,
.homonymie,
.plainlinks {
    color: var(--foreground-2) !important;
    background-color: transparent!important;
}

.quotebox {
    color: var(--foreground-2) !important;
    background-color: var(--background-3) !important;
    border-color: var(--border) !important;
}

.toc {
    background: transparent !important;
    border-color: var(--border) !important;
    border-style: solid !important;
    display: block !important;
    max-width: 300px !important;
}

.toctoggle {
    display: none !important;
}

body.mediawiki table.mbox-small-left {
    background-color: var(--background-2) !important;
}

.thumbinner,
.thumb,
.toccolours,
.graytable {
    background: transparent !important;
    border: 0px !important;
    font-size: 16px !important;
}

.thumbimage {
    border: 0px !important;
}

.thumbcaption {
    font-style: italic !important;
    margin: 7px 0px !important;
}

.thumbimage:hover {
    opacity: 0.85 !important;
}

.dablink {
    margin: 14px 0px !important;
    font-weight: 300 !important;
    font-size: 18px !important;
}

#siteSub,
.flaggedrevs_preview {
    display: none !important;
}

.ambox,
.tmbox,
.Note,
.informationbox {
    margin: 5px 0px !important;
    font-size: 18px !important;
    line-height: 1.5 !important;
    max-width: 550px !important;
    font-weight: 300 !important;
}

.ambox td,
.tmbox td {
    padding: 10px !important;
}

.ambox-text-small,
.mbox-text,
.Note td {
    font-size: 14px !important;
    font-weight: 300 !important;
    color: var(--foreground) !important;
}

.ambox-image,
.mbox-image {
    display: none;
}

#request_for_deletion,
.ambox-serious {
    background: rgba(255, 0, 0, 0.1) !important;
    border: 1px solid rgba(255, 0, 0, 0.15) !important;
    color: #a00000 !important;
}

.tmbox-notice,
.tmbox-move,
.Note {
    background: rgba(150, 75, 0, 0.1) !important;
    border: 1px solid rgba(150, 75, 0, 0.15) !important;
}

.informationbox {
    background: var(--background-2) !important;
    padding: 10px !important;
    font-size: 14px !important;
}

.shortcutbox {
    background: var(--background-4) !important;
}

.editOptions {
    background: var(--background-2) !important;
    border: 0px !important;
}

.oo-ui-toolbar-bar {
    background-color: var(--background-2) !important;
}

.oo-ui-popupToolGroup.oo-ui-widget-enabled > .oo-ui-popupToolGroup-handle:hover,
.oo-ui-tool.oo-ui-widget-enabled > .oo-ui-tool-link:hover {
    background-color: var(--background-4);
}

.oo-ui-toolbar-actions .oo-ui-popupToolGroup.oo-ui-widget-enabled > .oo-ui-popupToolGroup-handle:hover {
    border-color: var(--background-5);
}

.oo-ui-toolbar-bar .oo-ui-iconElement-icon,
.oo-ui-popupToolGroup.oo-ui-widget-enabled > .oo-ui-popupToolGroup-handle .oo-ui-iconElement-icon,
.oo-ui-popupToolGroup.oo-ui-widget-enabled > .oo-ui-popupToolGroup-handle .oo-ui-indicatorElement-indicator {
    filter: invert(1);
}

.oo-ui-barToolGroup-tools.oo-ui-toolGroup-enabled-tools .oo-ui-tool.oo-ui-widget-disabled.oo-ui-flaggedElement-primary > .oo-ui-tool-link,
.oo-ui-barToolGroup-tools.oo-ui-toolGroup-disabled-tools .oo-ui-tool.oo-ui-flaggedElement-primary > .oo-ui-tool-link {
    background-color: var(--background-4);
}

.oo-ui-tool.oo-ui-widget-enabled.oo-ui-flaggedElement-primary.oo-ui-flaggedElement-progressive > .oo-ui-tool-link {
    color: var(--foreground-2) !important;
}

.ve-ui-mwSaveDialog-summary,
.oo-ui-textInputWidget .oo-ui-inputWidget-input,
.ve-ui-mwSaveDialog-options,
.oo-ui-menuSelectWidget,
.oo-ui-dropdownWidget.oo-ui-widget-enabled .oo-ui-dropdownWidget-handle,
.oo-ui-tagMultiselectWidget.oo-ui-widget-enabled {
    background-color: var(--background);
    color: var(--foreground-2);
}

.oo-ui-dropdownWidget.oo-ui-widget-enabled .oo-ui-dropdownWidget-handle:hover,
.oo-ui-menuSelectWidget .oo-ui-labelElement:hover {
    background-color: var(--background-2);
    color: var(--foreground-2);
}

.oo-ui-tagMultiselectWidget-handle > .oo-ui-tagMultiselectWidget-content > input {
    color: var(--foreground-2);
}

.oo-ui-pendingElement-pending {
    background-color: var(--background);
    background-image: linear-gradient(135deg, black 25%, transparent 25%, transparent 50%, black 50%, black 75%, transparent 75%, transparent);
}

.ve-ui-changeDescriptionsSelectWidget > .oo-ui-optionWidget-highlighted {
    background-color: var(--background-5);
}

.ve-ui-diffElement-attributeChange del {
    background-color: var(--diff-del);
    box-shadow: 0 0 0 1px var(--diff-del);
}

.ve-ui-diffElement-attributeChange ins {
    background-color: var(--diff-ins);
    box-shadow: 0 0 0 1px var(--diff-ins);
}

.catlinks {
    background: transparent !important;
    border: 1px solid var(--border) !important;
    font-size: 16px !important;
}

.infobox,
.tright,
.toccolours {
    color: var(--foreground) !important;
    border: 0px !important;
    background: transparent !important;
    border-collapse: collapse !important;
}

.infobox td,
.infobox th,
.tright td,
.tright th,
.toccolours td,
.toccolours th {
    padding: 5px !important;
    background: var(--background-4) !important;
    font-weight: 400 !important;
}

.infobox_v3 {
    background: var(--background-4) !important;
    border-color: transparent !important;
}

.infobox_v3 .entete div {
    background-color: var(--background-4) !important;
    color: var(--foreground) !important;
}

.image img:hover {
    opacity: 0.85 !important;
}

.globegris {
    background: transparent !important;
}

.mw-editsection-bracket,
.references-small b,
.mw-cite-backlink,
.plainlinksneverexpand {
    display: none !important;
}

#floating_object {
    display: none;
}

.navbox,
.nowraplinks {
    margin: 10px 0px !important;
    border: 0px !important;
    background: transparent !important;
}

.navbox td,
.navbox th,
.nowraplinks td,
.nowraplinks th {
    padding: 9px 6px !important;
    font-size: 14px !important;
    font-weight: 300 !important;
    border: 1px solid var(--background-2) !important;
}

.vertical-navbox th {
    background-color: var(--background-5) !important;
}

.NavFrame {
    border: 0px !important;
    background: transparent !important;
    padding: 0px !important;
}

.NavHead {
    margin: 10px 0px !important;
    padding: 12px !important;
    font-size: 16px !important;
    font-weight: 700 !important;
    background: transparent !important;
}

.NavContent {
    background-color: #ffffff1a !important;
}

.Boxmerge {
    border: 0px !important;
}

#coordinates {
    display: none !important;
}

#mw-fr-revisiontag,
#mw-fr-reviewnotice {
    display: none !important;
}

div#footer {
    margin-left: 14em !important;
    padding: 1.25em 1.5em 1.5em !important;
}

#footer-info-copyright,
#footer-info-lastmod {
    color: var(--foreground-footer) !important;
}

.oo-ui-window-frame,
.oo-ui-popupWidget-popup {
    color: var(--foreground) !important;
    background-color: var(--background-5) !important;
}

.oo-ui-buttonElement-button .oo-ui-iconElement-icon {
    filter: invert(100%);
}

.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button {
    background-color: var(--background-2) !important;
    border-color: var(--background-2) !important
}

div.mw-revslider-revision-wrapper-down.mw-revslider-revision-hovered {
    color: #d6a100 !important;
}

div.mw-revslider-revision-wrapper-up.mw-revslider-revision-hovered {
    color: #1e6db8 !important;
}

.mw-revslider-revision-older {
    background-color: var(--diff-old) !important;
}

.mw-revslider-revision-intermediate {
    background-color: var(--background-5) !important;
}

.mw-revslider-revision-newer {
    background-color: var(--diff-new) !important;
}

.mw-revslider-revision {
    background-color: var(--background-5) !important;
}

del.diffchange.diffchange-inline {
    background-color: var(--diff-old) !important;
}

ins.diffchange.diffchange-inline {
    background-color: var(--diff-new) !important;
}

.diff-context {
    color: #999 !important;
}

td.diff-otitle,
td.diff-ntitle {
    background-color: var(--background-4) !important;
}

.lang-list-button {
    outline-color: var(--background-3) !important;
}

.lang-list-content {
    background-color: var(--background-5) !important;
}

.bookshelf span {
    background-color: var(--background-5) !important;
}

.lang-list-container {
    background-color: var(--background-4) !important;
}

.central-textlogo__image {
    filter: invert(100%);
}

.localized-slogan {
    color: var(--foreground) !important;
}

.svg-Wikimedia-logo_black {
    filter: invert(100%);
}

.hll {
    background-color: var(--background-3) !important;
}

kbd {
    background-image: linear-gradient(to top, var(--background-5) 0%, var(--background) 50%, var(--background-5) 100%) !important;
    box-shadow: 0.1em 0.2em 0.2em var(--background) !important;
    border: 1px solid var(--border) !important;
}

[style*="background:#ccc"] {
    background: var(--background-4) !important
}

[style*="background:#eeeeee"],
[style*="background:#efefef"] {
    background: var(--background-3) !important
}

/* Central Notice Banner */
.cnotice {
    background-color: var(--background-4) !important;
    border: 1px solid var(--border);
}

.cnotice-toggle-box {
    filter: invert(100%);
}

div[style*="background:#FCFCFC"] {
    background: var(--background) !important
}

/* Preferences */
.client-js #preferences {
    border: 1px solid var(--border) !important;
    background-color: var(--background-5) !important;
}

.client-js #preferences legend {
    color: var(--foreground);
}

.oo-ui-menuLayout-content {
    background-color: var(--background) !important;
}

.oo-ui-tabSelectWidget-framed {
    background-color: var(--background-2) !important;
}

.oo-ui-tabSelectWidget-framed .oo-ui-tabOptionWidget.oo-ui-optionWidget-selected,
.oo-ui-menuOptionWidget.oo-ui-optionWidget-selected {
    background-color: var(--background-4);
}

.oo-ui-tabSelectWidget-framed .oo-ui-tabOptionWidget.oo-ui-widget-enabled.oo-ui-optionWidget-selected:hover,
.oo-ui-tabSelectWidget-framed .oo-ui-tabOptionWidget.oo-ui-widget-enabled:hover {
    background-color: var(--background-5);
}

.mw-prefs-buttons,
.oo-ui-dropdownWidget.oo-ui-widget-enabled.oo-ui-dropdownWidget-open .oo-ui-dropdownWidget-handle {
    background-color: var(--background-3);
}

.oo-ui-checkboxInputWidget [type='checkbox'] + span,
.oo-ui-radioInputWidget [type='radio'] + span {
    background-color: var(--background-black);
}

