css:
@-moz-document domain("autokronika.pl") {
html,body{
    background: #000;
    color: #fff;
}
:root{
    scrollbar-color: #fff #000;
}
a,h1,h2,h3,h4{
    color: #fff !important;
}
a:hover,p,span{
    color: #fff !important;
}
a:hover{
    text-decoration: underline !important;
}
*{
    box-shadow: none !important;
	text-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],input[type="url"],textarea,select{
    background: #000 !important;
    color: #fff !important;
    border: 1px solid #fff !important;
}
input[type="submit"]:hover{
    background: #fff !important;
    color: #fff !important;
}
/*HEADER*/
.branding-wrapper,.main-navigation{
    background: #000;
}
.site-title a,.main-navigation a{
    color: #fff !important;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.hentry,.widget-area .widget,.comment-navigation .nav-previous, .posts-navigation .nav-previous, .post-navigation .nav-previous,.post-navigation .nav-next{
    background: #000;
}
.entry-title a{
    color: #fff !important;
}
.widget-area .widget-title::after{
    background: #fff;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.entry-title{
    color: #fff !important;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
/*SZUKAJ*/
.page-title{
    background: #000;
}
/*SZUKAJ*/
/*LOGOWANIE*/
.login form{
    background: #000;
}
.login h1 a{
    filter: invert(95%) sepia(100%) saturate(0%) hue-rotate(91deg) brightness(104%) contrast(105%);
}
/*LOGOWANIE*/
/*PODSTRONY*/
/*FOOTER*/
.site-footer, .footer-widget-area{
    background: #000;
}
.site-footer:after{
    content: "Ciemny motyw by Maciejozaman";
    cursor: none;
}
.site-footer{
    color: #fff !important;
}
.site-footer a{
    text-decoration: underline !important;
}
/*FOOTER*/
}
