css:
/* i really want this to be global */
.rightPage, .filmspustiteh2, .filmspustite, .prnInfo, .reklama2, .komentare, .fb_iframe_widget, .spustitNavodHref, footer, header, .reklamaLeftFixed_etarget, .reklamaRightFixed { display: none !important; }
.leftPage { width: 100%; }
.film .video1, .film .video2, .film .video3, .film .video4, .film .video5, .film .video6 { height: 690px !important; }
body { background-color: #131212; color: #fff; background-image: url('https://fazen.sk/files/tomoviesbg.jpg'); background-size: cover; background-position: center center; background-attachment: fixed; }
p, span, strong, .nadpisvypis, em, .name, .main h2 { color:#fff !important; }
.itemSeriaVypis a, .itemSeriaVypis a strong, .navrhy li span { color: #000 !important; }
.oblubene, .videne { background: #333 !important; }
.videne.active { background: #e70000 !important; }
.epizoda_next, .epizoda_prev { background: #333 !important; }
.film .odkazy, .othersViac { background: transparent !important; }
ul.menu { background: #2a2c2e; }
ul.menu li { border-right: 0; }
ul.menu li.secondpage { border-left: 0; }
ul.menu li a { color: #fff; }
.filmyroky { background: #3a3d40; }
.filmyroky ul li a, .dalsiNazov h3 { color: #fff; }
.film ul.menuZdroje li span { background: #3a3d40; }
.checkboxRadioGraphic .checkboxG { background: #3b3b3b; }
.oblubene.active { background: #39aa35 !important; }
.film .nahlasit .nadpis { background: darkRed; }
.menuT { top: 0px; background: #2a2c2e; }
.headertop { margin-bottom: 40px; }
.filmyroky { margin-top: 8px; }
.menuT ul.menu li { border-right: 1px solid #3a3d40; }
.menuT ul.menu li.nahodnyfilm, .menuT ul.menu li.secondpage { border-left: 0px solid #3a3d40; }
.menuT ul.menu li a { color: #fff; }
.film ul.menuZdroje li.sub em, .film ul.menuZdroje li.sub ul li span { background: #333; }
.nahlasit { text-align: center; }
