css:
@-moz-document url-prefix('https://www.nushell.sh') {
body {
    font: 18px/1.5 "OpenSans", "Helvetica Neue", Helvetica, Arial, sans-serif;
    color: #FFF;
    background: #222;
}

code.language-plaintext, pre {
    font-family: Hasklig, FiraCode, Consolas, Menlo, Monaco, Hack, Roboto Mono, Droid Sans Mono, Liberation Mono, DejaVu Sans Mono, monospace;
    color: #11FF1170;
    font-size: 100%;
    border-radius: 2px;
    text-shadow: 0 1px 0 black;
    border: 1px solid #444;
    background: #202020;
    
}

pre u {
    text-decoration: none;
    color: inherit;
    font-weight: normal;
}
/*    font-variant-ligatures: common-ligatures; */
table {
    color: #DDD;
}

table td {
    padding: 0.1em 0 0.1em 0.5em;
    border: 1px solid #DDD;
}

table th {
    background-color: #333;
    color: #DDD;
    border: 1px solid #DDD;
}
.tab, button {
    background-color: #0000;
    
    color: #DDD;
}
.information {
    color: #DDD;
    border-radius: 2px;
    background: #222;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    background: #222;
    color: #FFF;
    margin: 0px 0 0.5em 0;   
}

.tabcontent {
    border-top: 1px;
}

a {
    color: #11FF1170;
    font-weight: bold;
}
a:hover {
    color: #FFD7B1;
}
#header nav a:hover, #header nav a:focus {
    color: #FFD7B1;
}
}
