css:
@-moz-document url-prefix("https://hackage.haskell.org"), url-prefix("https://hoogle.haskell.org") {
body {
    background: #222;
    color: #EEE;
}
#synopsis ul, #synopsis ul li.src {
    background-color: #333;
}
a[href]:link {
    color: #28D69C;
}
a[href]:hover {
    text-decoration: none;
}
a.def {
    color: #30B040;
    text-decoration: none;
}
a:link,
a:visited {
    /*border-bottom: 1px dotted #8A6DCC70;*/
    border-bottom: none;
    text-decoration: none;
}
a:hover,
a.hover-highlight {
    background-color: transparent;
    border-bottom: 1px solid;
    text-decoration: none;
}
:target, :target:hover{
    background-color: #333;
    background: none;
    /*background: linear-gradient(to bottom, transparent 0%, transparent 0%, #fffcc8 0%, #fffaaa 100%);*/
}
#interface .src .selflink,
#interface .src .link {
}
div #properties {
    background: #222;
    border: #0E774A;
    border-width: 1px;
    color: #EEE;
}
#contents-list {
    background: #333;
}
pre,
code,
kbd,
samp,
.src {
    font-family: Hasklig, FiraCode, monospace;
    border: 1px solid #444;
    background: #222;
    color: #FFF;
}
code,
table.info {
    color: #FFF;
    background: #333;
    border: #0E774A;
    border-width: 1px;
}
.subs .subs p.src {
    border: none;
    background-color: #333;
}
.hs-identifier {
    color: #30B040;
}
.hs-identifier.hs-var {
    color: #FFE1A2;
}
.hs-identifier.hs-type {
    color: #28D69C;
}
.hs-glyph,
.hs-special,
.hs-operator {
    color: #FFFFFF;
}
.hs-keyword {
    color: #458FDD;
}
.hs-string,
.hs-char {
    color: #93674a;
}
.hs-comment {
    color: #579C89;
}

.hs-number {
    color: #F88AF0;
}
.hs-escape {
    color: #2FD1FF;
}
.hs-cpp {
    color: #FFF;
}
.hs-pragma {
    color: #8A6DCCC0;
}

}
