/* ==UserStyle==
@name           nicolive ad-block dark
@version        1.0.2
@description    ニコ生の広告削除＆ダークテーマ
@author         shu_1167
==/UserStyle== */

/* 広告削除 */
aside[class*="___banner-panel___"],
aside[class*="___program-information-ad-area___"],
aside[class*="___banner-ad___"],
 div[class*="___zapping-widget___"],
iframe[id^="ads_"],
a[class*="___app-download-banner___"] {
    display: none !important;
}



/* 背景色 */
div[class*="___tag-widget___"] ul[class*="___tag-list___"] li span,
aside[class*="___billboard-banner___"],
button[class*="___comment-tab___"]:not([aria-selected=true]),
button[class*="___program-recommend-tab___"]:not([aria-selected=true]) {
    background-color: #303030 !important;
}

a[class*="___content___"],
section[class*="___player-setting-view___"] > header,
section[class*="___ng-panel___"] > header,
div[class*="___ng-data-grid___"] header *:not(hr) {
    background-color: #202020 !important;
}

div[class*="___preview___"] {
    background-color: #1D1D1D;
}

div[class*="___program-information-area___"],
div[class*="___tab-area___"],
div[class*="___player-area___"],
div[class*="___broadcast-participation-area___"],
div[class*="___program-information-header-area___"],
div[class*="___program-summary___"],
div[class*="___player-status___"],
div[class*="___header-table___"],
button[class*="___expand-button___"],
div[class*="___footer-area___"],
nav a,
div[class*="___official-locked-item-area___"] button img,
section[class*="___player-setting-view___"],
section[class*="___ng-panel___"] {
    background-color: #1A1A1A !important;
}

div[class*="___watch-page___"] {
    background-color: #1A1A1A;
}

form[class*="___follow-toggle-form___"] button,
a[class*="___contents-tree-anchor___"],
a[class*="___community-page-anchor___"],
button[class*="___comment-tab___"][aria-selected=true],
button[class*="___program-recommend-tab___"][aria-selected=true] {
    background-color: #0F0F0F !important;
}

button[class*="___popup-toggle-button___"] {
    background-color: lightgray !important;
}

div[class*="___overlay___"] {
    background-color: hsla(0, 0%, 0%, .8) !important;
}




/* hover動作 */
div[class*="___table-row___"]:hover,
div[class*="___ng-data-grid___"] div[class*="___table-row___"]:hover {
    background-color: #B9B9B9 !important;
    color: black !important;
}

a[class*="___content-card___"]:hover,
div[class*="___program-card___"]:hover,
div[class*="___program-card___"]:hover div,
/* div[class*="___toggle-button-field___"]:hover, */
/* section[class*="___base-select-menu___"] ul div:hover, */
div[class*="___menu-button-field___"]:hover {
    background-color: #B9B9B9 !important;
}

a[class*="___content-card___"]:hover p,
div[class*="___program-card___"]:hover a,
div[class*="___program-card___"]:hover span,
div[class*="___menu-button-field___"]:hover *,
div[class*="___toggle-button-field___"]:hover label,
section[class*="___base-select-menu___"] ul div:hover label {
    color: black !important;
}

button[class*="___popup-toggle-button___"]:hover {
    background-color: #9F9F9F !important;
}

button[class*="___ng-setting-popup-button___"]:hover svg path,
div[class*="___comment-data-grid___"] div[class*="___table-row___"]:hover span:before {
    fill: #9F9F9F !important;
}

button[class*="___comment-tab___"]:not([aria-selected=true]):hover,
button[class*="___program-recommend-tab___"]:not([aria-selected=true]):hover {
    background-color: #3A3A3A !important;
}

div[class*="___official-locked-item-area___"] button:hover img {
    background-color: black !important;
    filter: brightness(500%) !important;
}

div[class*="___toggle-button-field___"]:hover button[aria-pressed=false]:before {
    background-color: hsla(0, 0%, 100%, .7) !important;
}

div[class*="___toggle-button-field___"]:hover button[aria-pressed=false]:after {
    background-color: white !important;
}




/* 文字色 */
div[class*="___label___"],
ul[class*="___tag-list___"] li span a,
div[class*="___description___"],
h1[class*="___program-title___"] span,
span[class*="___header-cell___"],
button[class*="___edit-button___"],
div[class*="___footer-area___"] p,
button[class*="___comment-tab___"][aria-selected=true],
button[class*="___program-recommend-tab___"][aria-selected=true],
div[class*="___ng-data-grid___"] div[class*="___table-row___"],
span[class*="___ng-item-count___"],
button[class*="___ng-delete-button___"] {
    color: lightgray !important;
}

li[class*="___nicoad-count___"] span button,
li[class*="___gift-count___"] span button,
time[class*="___onair-time___"],
span[class*="___airtime___"],
a[class*="___contents-tree-anchor___"],
a[class*="___group-name-anchor___"],
a[class*="___community-page-anchor___"],
div[class*="___disabled-message___"],
section[class*="___player-setting-view___"] header *,
section[class*="___ng-panel___"] header *,
div[class*="___menu-button-field___"] svg,
div[class*="___program-card___"] a,
button[class*="___expand-button___"] {
    color: #B9B9B9 !important;
}

div[class*="___watch-page___"] {
    color: #B9B9B9;
}

a[class*="___content___"],
p[class*="___room-name___"],
a[class*="___channel-name-anchor___"],
span[class*="___supplier-name___"],
a[class*="___user-name___"],
span[class*="___level___"],
div[class*="___program-information-side-area___"] section h2,
div[class*="___content-area___"] p,
button[class*="___comment-tab___"]:not([aria-selected=true]),
button[class*="___program-recommend-tab___"]:not([aria-selected=true]),
button[class*="___close-button___"],
section[class*="___player-setting-view___"] div,
a[class*="___name-label___"]:not(:hover) {
    color: #9F9F9F !important;
}



/* SVGのfill色 */
button[class*="___edit-button___"] svg path,
button[class*="___ng-setting-popup-button___"] svg path,
button[class*="___close-button___"] svg path {
    fill: lightgray !important;
}

ul[class*="___program-statistics-list___"] svg path,
a[class*="___contents-tree-anchor___"] svg path,
li[class*="___gift-count___"] svg path:first-child,
button[class*="___quote-request-button___"] svg {
    fill: #8A8A8A !important;
}

li[class*="___gift-count___"] svg path:last-child {
    fill: none !important;
}




/* imgの色調整 */
div[class*="___official-locked-item-area___"] button img {
    filter: brightness(200%) !important;
}



/* その他 */
/* タイトル周辺にあるshadow削除 */
div[class*="___program-summary___"] {
    box-shadow: none;
}

/* 下部コミュニティ欄のすべて表示ボタンの効果を消す */
button[class*="___expand-button___"] {
    background: none !important;
}

/* 未視聴タイムシフトの通知を消す */
div[class*="___timeshift-notification-bar___"] {
    display: none !important;
}

/* 放送画面周辺のborderの角を丸くする */
div[class*="___broadcast-participation-area___"] {
    border-radius: 0 0 4px 4px;
}
div[class*="___player-status___"] {
    border-radius: 0 4px 0 4px;
}

/* NG設定画面の枠を表示する */
div[class*="___ng-data-grid___"] > header span {
    border-left: 1px solid;
}

/* ニコニ広告の上に出る吹き出しを消す */
div[class*="___promotion-balloon___"][aria-hidden=false] {
    display: none !important;
}
