img[src="http://img.photo-forum.net/site_design/logo.gif"]{display:none !important;}
div#header_img_left {background-image: url(http://img507.imageshack.us/img507/5166/leftjpgheader.gif) !important;}

.PhorumListRow {background-color: #eee !important;}
