css:
@-moz-document url-prefix("https://servicioenlinea.ug.edu.ec/SIUG"){
body {
    background: #14161B !important;
}
.title {
    background-color: #1F2539!important;
}
.loginDisplay {
    font-size: 1.1em;
    display: block;
    text-align: center;
    padding: 5px;
    color: White;
    background-color: #414A67;
    margin: 10px;
    border-radius: 10px;
    width: 150px;
}

td {
    width: 35%;
    text-align: -webkit-right;
}

div.hideSkiplink {
    background-color: #1F2539;
    width: 100%;
    font-size: 1.5rem;
}
.page {
    width: 960px;
    background-color: #1F2539;
    margin: 20px auto 0px auto;
    border: 1px solid #1F2539;
}
.style3 {   
    color: #1F2539;
}

.form-control {
    display: block;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #555;
    background-color: #1F2539;
    background-image: none;
    border: 0px solid #ccc;
    border-radius: 10px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
    -webkit-transition: border-color ease-in-out .15s, -webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s, box-shadow ease-in-out .15s;
}

.panel {
    margin-bottom: 20px;
    background-color: #414A67;
    color: white;
    font-size: 16px;
    border: 1px solid transparent;
    border-radius: 4px;
    -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
    box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
}

.btn {
    display: inline-block;
    padding: 6px 12px;
    margin-bottom: 0;
    font-size: 16px;
    font-weight: normal;
    line-height: 1.42857143;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    -ms-touch-action: manipulation;
    touch-action: manipulation;
    cursor: pointer;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    /* background-color: #006B93; */
    border: 1px solid transparent;
    border-radius: 10px;
}

.bg-pink-400 {
    background-color: #006B93;
    border-color: #006B93;
    color: #fff;
}

a:link, a:visited {
    color: #006B93;
}

.text-left {
    text-align: left;
    color: #eaeaea;
}

.style2 {
    width: 1360px;
    height: 150px;
    background-color: #14161b;
    opacity: 0%!important;
}

.header {
    position: relative;
    margin: 0px;
    padding: 0px;
    background: #14161b;
    background-image: url(https://i.pinimg.com/originals/54/36/38/543638917fcbdec17d4eca0db076ce79.png);
    width: 100%;
    background-attachment: initial;
    background-position: top left;
    background-repeat: no-repeat;
    /* height: 200px; */
}

.title {
    background-color: #1F2539!important;
    display: block;
    float: left;
    text-align: left;
    width: auto;
    margin-top: 20px;
}

.text-center img {
    opacity: 0%;
}

fieldset .text-center {
    background-image: url(https://i.pinimg.com/originals/9d/7a/ef/9d7aef3346be446cbe2498464963bfe5.png);
    height: 160px;
    margin-bottom: 15px;
  border-radius: 10px;}}
