css:
/*  Home  */

@-moz-document url("http://www.onemanga.com/"), url("http://feedback.onemanga.com/")

{

  div#content-main > div.news-entry:first-child + div.news-entry, div#wrap-side, div.side-wrapper:first-child

     {
     display: none !important;
     } 



  div#wrap-index

     {
     width: 780px !important;
     }

}









/*  Manga List */

@-moz-document url-prefix("http://www.onemanga.com/directory/")

{

  div.side-wrapper:first-child, div.side-wrapper + div.side-wrapper + div.side-wrapper + div.side-wrapper + div.side-wrapper

     {
     display: none !important;
     } 



  div.alpha-anchor, div.legend
 
     {
     border-color: #999999 !important;
     }



  a

     {
     color: #dddddd !important;
     }

}









/*  Manga List Top */

@-moz-document url("http://www.onemanga.com/directory/top/"), url("http://www.onemanga.com/directory/pop/"), url("http://www.onemanga.com/directory/shounen/pop/"), url("http://www.onemanga.com/directory/shoujo/pop/")

{

  div.side-wrapper:first-child, div.side-wrapper + div.side-wrapper + div.side-wrapper + div.side-wrapper

     {
     display: none !important;
     } 

}









/*  Search */

@-moz-document url("http://feedback.onemanga.com/directory/search/"), url("http://www.onemanga.com/directory/search/")

{

  div.side-wrapper:first-child, div.side-wrapper + div.side-wrapper + div.side-wrapper + div.side-wrapper + div

     {
     display: none !important;
     } 



  div.alpha-anchor, div.legend
 
     {
     border-color: #999999 !important;
     }



  a

     {
     color: #dddddd !important;
     }

  input[value="Filter Manga List"], input[value="Reset Selections"]

     {
     -moz-border-radius: 5px !important;
     background-color: #333333 !important;
     color: #bbbbbb !important;
     }

}









/*  Recent Updates  */

@-moz-document url("http://www.onemanga.com/recent/")

{

  div#content-side

     {
     display: none !important;
     }



  div#content-main

     {
     width: 740px !important;
     }



  table.ch-table

     {
     width: 730px !important;
     }



  .ch-date

     {
     width: 150px !important;
     }

}









/*  Contact Us  */

@-moz-document url("http://www.onemanga.com/contactus/")

{

  div#wrap

     {
     width: 780px !important;
     }



  div#site-banner

     {
     background-color: #000000 !important;
     }


  div#content-side

     {
     display: none !important;
     }



  div#content-main

     {
     width: 740px !important;
     }



  input#id_email, input#id_subject

     {
     -moz-appearance: none !important;
     width: 400px !important;
     background-color: #444444 !important;
     color: #bbbbbb !important;
     }



  textarea#id_content

     {
     -moz-appearance: none !important;
     width: 715px !important;
     height: 275px !important;
     background-color: #444444 !important;
     color: #bbbbbb !important;
     }



  input[value="Submit"], input[value="Cancel"]

     {
     -moz-appearance: none !important;
     background-color: #333333 !important;
     color: #bbbbbb !important;
     -moz-border-radius: 5px !important;
     }

}









/*  Request Manga  */

@-moz-document url("http://www.onemanga.com/request-manga/")

{
  
  div#wrap

     {
     width: 780px !important;
     }



  div#content-side

     {
     display: none !important;
     }



  div#content-main

     {
     width: 740px !important;
     }



  input#id_manga_name, input#id_sample_url

     {
     -moz-appearance: none !important;
     width: 400px !important;
     background-color: #444444 !important;
     color: #bbbbbb !important;
     }



  input[value="Submit Manga Request!"], input[value="Cancel"]

     {
     -moz-appearance: none !important;
     background-color: #333333 !important;
     color: #bbbbbb !important;
     -moz-border-radius: 5px !important;
     }

}











/*  Submit Manga */

@-moz-document url("http://www.onemanga.com/submit-manga/")

{

   div.side-wrapper

     {
     display: none !important;
     }



   div#content-main

     {
     width: 740px !important;
     }



   textarea#id_description

     {
     -moz-appearance: none !important;
     background-color: #555555 !important;
     color: #bbbbbb !important;
     }

}












/*  All Pages  */

@-moz-document domain("www.onemanga.com"), domain("feedback.onemanga.com")

{

  body

     {
     color: #aaaaaa !important;
     background-image: none !important;
     background-color: #000000 !important;
     position: relative !important;
     }



  div#top-line
 
     {
     background-color: #000000 !important;
     text-align: center !important;
     border: none !important;
     }



  div
   
     {
     -moz-border-radius: 10px !important;
     }



  span

     {
     color: #dddddd !important;
     background-color: #111111 !important;
     }



  a

     {
     background-color: transparent !important;
     text-decoration: none !important;
     }



  a:link

     {
     color: #B0C4DE !important;
     }



  a:visited

     {
     color: #778899 !important;
     }



  a:hover

     {
     color: #F0F8FF !important;
     }
    


  div#top-nav

     {
     text-align: center !important;
     }



  span.entry-time, span.entry-postedby, span.posted-by

     {
     font-weight: bold !important;
     }



  div#content-main, div.side-wrapper, div.side-list, div.contact-form, div.news-entry, td, div#chapter-cover, div.side-header, div.removed-note, div.filter-box, div[style="clear: both;"], div.page-navigation

     {
     background-color: #111111 !important;
     }



  div.removed-note

     {
     background-color: #222222 !important;
     border: none !important;
     color: #bbbbbb !important;
     }



  div#header-dropdown, div.ad-section, div.license-note, select#manga_list, div.banner-chapter, div.note, div#footer, input[value="back"], input[value="next"], input[value="previous page"], input[value="next page"], div.warning

     {
     display: none !important;
     }



  input, option, select, textarea

     {
     border: none !important;
     }



  th
 
     {
     background-color: #222222 !important;
     border: none !important;
     color: #999999 !important;
     }









/* Manga Viewing */

  div#page-nav

     {
     font-size: 10px !important;
     width: 70% !important;
     }



  div.chapter-navigation

     {
     text-align: center !important;
     }
     


  input

     {
     -moz-appearance: none !important;
     background-color: #555555 !important;
     color: #bbbbbb !important;
     }



  select[name="page"], select[name="chapter"]

     {
     -moz-appearance: none !important;
     background-color: #444444 !important;
     color: #bbbbbb !important;
     }



   a#id_report

     {
     position: relative !important;
     top: 20px !important;
     }



   div#page-content

     {
     line-height: 1em !important;
     }



   div#page-content > h1

     {
     position: absolute !important;
     top: 5px !important;
     right: 5px !important;
     font-size: 12px !important;
     }



   div.one-page

     {
     position: relative !important;
     top: 10px !important;
     }



   div.chapter-navigation

     {
     position: relative !important;
     top: -8px !important;
     }











