css:
@-moz-document url-prefix("http://forum.pears-project.com")
{
	.xb-page-wrapper {
		background: #ffffff;
		border: none;
	}
	.message-cell.message-cell--user, .message-cell.message-cell--action {
		position: relative;
		background: #f9f9f9;
		min-width: 0;
		border: none;
	}
	.block-container {
		color: #fff;
		background: #fff;
		border: none;
	}
	.message-cell.message-cell--main {
		background: #171b24;
		/* CHANGE N1 */
		border: none;
	}
	.message-userArrow:after {
		border-right-color: #171b24;
		border: none;
	}
	.pairs.pairs--justified > dt {
		float: left;
		max-width: 100%;
		margin-right: 15px;
		border: none;
	}
	.pairs > dt {
		color: #bd877b;
		font-weight: bold;
		border: none;
	}
	.pairs > dd {
		color: #bd877b;
		border: none;
	}
	.message-header .message-attribution {
		color: #171b24;
		background: #f9f9f9;
		border: none;
		text-shadow: none;
	}
	div.TS {
		color: #b9b9b9;
		background: #343535 !important;
		border: none;
	}
	.p-title {
		color: #f58e78;
	}
	.node .node-title a {
		color: #f58e78
	}
	.block--category .block-container .node {
		border: 1px solid #f58e78;
		box-shadow: inset 0px 1px 0px 0px #f58e78;
	}
	.block--category .block-body.nodedual .node {
		border-bottom: 1px solid #f58e78;
	}
	.node-footer {
		background: #f58e78;
		height: 1px;
	}
	.block-container .structItemContainer-group .structItem-title {
		color: #f58e78;
	}
	.p-breadcrumbs--parent .p-breadcrumbs {
		border: none;
		background: #f58e78;
	}
	.block-row.block-row--minor {
		background: #f9f9f9;
	}
	.block-row {
		background: #882c2c;
	}
	.banner-style_osnovatel {
		color: #333333;
		border-color: #333333;
	}
	.username--style5 {
		color: #333333;
	}
	.p-body-header a.button .button-text {
		display: none;
	}
	a {
		color: #f58e78;/*bd877b*/
		text-decoration: none;
	}
	.threadSeparator.sticky {
		font-size: 14px;
		color: rgba(199, 199, 199, 0.8);
		border-bottom: 1px solid #293040;
		padding: 20px;
		background: linear-gradient(0deg, #1d222d, #1a1f29);
		border: none;
		text-shadow: none;
	}
	.formRow > dt {
		background: #882c2c;
		border-color: #882c2c;
	}
	.formRow > dd {
		background: #882c2c;
		border-color: #882c2c;
	}
	.memberTooltip-header {
		background: #ffffff;
		border-color: #ffffff;
	}
	.tooltip--bookmark .tooltip-content, .tooltip--member .tooltip-content, .tooltip--share .tooltip-content {
		 color: #f58e78;
		 background: #B8B8B8;
		 border-color: #B8B8B8;
	}
	.formRow .iconic > input[type=checkbox]:checked ~ span.iconic-label {
		color: #f58e78;
	}
	.block-row.block-row--minor {
		background: #882c2c;
	}
}
