css:
@-moz-document url-prefix("https://tracker.conservice.com/") {
.material-card {
    background-color: #333;
    margin-top: 0 !important;
}
.material-card-body {
    background-color: #333;
    color: #ddd;
    border-color: #111;
    padding: 0;
    box-shadow: none;
}
.material-card-body-unpadded {
    background-color: #333;
    color: #eee;
}
#miscStatsCarousel .material-card-body {
    padding: 5px;
    color: #eee !important;
}
.card-header {
    border: none;
    background-color: transparent;
}
.progBars {
    padding: 10px;
}
.k-alt {
    background-color: rgba(0, 0, 0, 0.2) !important;
}
tr:hover {
    background-color: rgba(255, 255, 255, 0.25) !important;
}
td {
    border: none !important;
}
th, .k-grid-header, .k-grid-header-wrap {
    background-color: rgb(38, 55, 70) !important;
    color: #eee !important;
    border: none !important;
}
input {
    background-color: rgba(255, 255, 255, 0.05) !important;
    color: #eee !important;
    border: 1px solid #222 !important;
}
#WorkLogGrid {
    height: 810px !important;
}
button {
    margin-top: 1px !important;
    margin-bottom: 1px !important;
}
input::placeholder {
    color: #ccc !important;
    opacity: 1;
}
body.site {
    background-color: #222 !important;
    background: repeating-linear-gradient(45deg, rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.1) 2px, rgba(0, 0, 0, 0.25) 2px, rgba(0, 0, 0, 0.25) 4px);
}
.k-picker-wrap {
    background-color: rgba(255, 255, 255, 0.05) !important;
    color: #eee !important;
    border: none !important;
}
.k-select, .k-input, .k-button, .k-state-selected, .k-pager-nav {
    color: #eee !important;
    background-color: #333 !important;
    border: 1px solid #222 !important;
    margin: -1px;
}
.body-content {
    padding-left: 0px;
    margin-left:5px;
}
.sidebar {
    margin-top: 65px;
}
.smoother {
    background-color: rgb(38, 55, 70);
}
.smoother::before {
    background-color: rgba(255, 255, 255, 0.45);
    box-shadow: 2px 2px 5px rgba(0, 0, 0, 0.5);
}
.row .mh-100 {
    padding: 5px;
    margin: 5px;
}
.flex-column.col-3 {
    padding: 0;
    margin: 0;
    padding-top: 10px;
}
.flex-column {
    margin: 0;
    padding-right: 5px;
    padding-left: 5px;
}
#hoursGraph {
    padding-top: 5px;
}
#slides {
}
#overhead {
    margin-top: 22px;
}
text {
    fill: #eee;
}
.input-group-text {
    background-color: #333;
    color: #eee;
    border-color: #222;
}
.k-widget {
    background-color: #333;
    border: none;
    box-shadow: none;
    color: #eee;
}
.k-grid-header {
    border: none !important;
    box-shadow: 0 5px 3px rgb(0, 0, 0, 0.1);
}
.k-grid-content {
    overflow: auto;
}
.k-header, .k-dropdown-wrap {
    border: none;
    box-shadow: none;
}
.k-calendar table, .k-nav-today, .k-calendar-container {
    background-color: #333 !important;
    color: #eee !important;
}
.k-footer {
    border: none !important;
}
.k-nav-today {
    border: none !important;
}
.k-link {
    color: #eee !important;
}
.k-link:hover {
    color: #222 !important;
}
#WorkLogGrid > .k-header > div {
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    align-items: baseline;
}
#WorkLogGrid > div.k-header > div > span:nth-child(4) {
    margin: 10px !important;
}
#WorkLogGrid .k-widget {
    flex: 1;
}
}
