css:
@-moz-document url-prefix("https://nitrogensports.eu") {
/** Nitro red: #bc2d2c **/
/** Nitro yellow: #ffb129 **/
body {
    color: #fff!important;
}
a {
    color: #bc2d2c;
}
a:focus, a:hover {
    color: #A92928;
}
.main {
    background-color: #262626!important;
}
.table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th {
    background-color: #3D3D3D!important;
}
.table td, .table th {
    border-top: 1px solid #5a5e6d !important;
}

.chocolate-outer {
    background: #262626!important;
    border: 1px solid #5a5e6d !important;
    box-shadow: none!important;
}
.chocolate-content {
    border-top: none!important;
    background: #3D3D3D!important
}
.chocolate-header {
    background: #262626!important;
}

.chocolate-input-group, .chocolate-input-group-auto {
    background-color: #262626!important; 
    border: 1px solid #5a5e6d!important;
}
.chocolate-input-group i, .chocolate-button i, .chocolate-button-auto i {
    color: #a6a6a6;
}
.chocolate-textbox::placeholder {
    color: #a6a6a6!important;
}
.chocolate-button, .chocolate-button-auto {
    background-color: #262626!important; 
    border-bottom-color: #5c6070!important;
    border-top-color: #73788c!important;
    border-left: 1px solid #5c6070!important;
    border-right: 1px solid #5c6070!important;
}

/** Welcome **/
#landing-image {
    background-image: none!important;
}
#landing-logo-new {
    background-image: url('https://nitrogensports.eu/assets/images/logo_new.png');
    background-size: contain;
}
#landing-tagline-new {
    color: #dcdcdc;
}
#landing-greeting {
    color: #fff!important;
}
#landing-sub-greeting1 {
    color: #dcdcdc;
}
#landing-welcome-id {
    color: #ffb129!important;
}
#landing-welcome-box p {
   color: #dcdcdc; 
}
#landing-welcome-box p a {
    color: #bc2d2c!important;
}

/** Welcome moda **/
#modal-welcome {
    background: #3D3D3D!important;
}
#modal-welcome-logo {
    height: 45px;
    background-image: url('https://nitrogensports.eu/assets/images/logo_new.png');
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
}


/** Bet page **/
#events-empty div {
    color: #fff!important;
}
#events-empty .sub {
    color: #dcdcdc!important;
}
.page-title-dark {
    color: #fff!important;
}
.page-title-dark small {
    color: #bc2d2c!important;
}
#find-games-title, #favourite-league, .favourite-league__container::before {    
    background-color: transparent;
}
#favourite-league.favourite-league--add, #favourite-league:hover:not(.animate) {
    background-color: #bc2d2c;
}
#favourite-league__label {
    display: none!important;
}

#period-tabs {
    border-bottom: 1px solid #6f6f6f !important; 
}
.period-tab a {
    color: #bc2d2c!important;
}
.period-tab a:hover {
    background: #3D3D3D!important;
    border: 1px solid #6f6f6f !important;
}

.period-tab.active a, .period-tab.active a:hover {
    background: #3D3D3D!important;  
    border: 1px solid #6f6f6f !important; 
    color: #fff!important;
}

#landing-stripe2 {
    background-color: #262626!important;
    border: none!important;
}
#landing-bitcoin-text {
    color: #fff;
}
#landing-bitcoin-text-small {
    color: #dcdcdc;
}
.landing-span-new {
    color: #dcdcdc;
}
.landing-span-header-new {
  color: #fff;  
}
#landing-stripe3 {
    background-color: #3D3D3D!important;
    border: none!important;
}
#landing-what-bitcoin-text, #landing-buy-bitcoin-text-small {
    color: #fff;
}
#landing-what-bitcoin-text-small, .landing-what-bitcoin-point-text {
    color: #dcdcdc!important; 
}
#landing-stripe4 {
    background-color: #262626!important;
    border: none!important;
}

/** Event search **/
#event-search {
    box-shadow: none!important;
}

/** Event **/
.event {
    border: 1px solid #5a5e6d !important;
    box-shadow: none!important;
}

.event-header {
    padding: 5px 0 5px 0!important;
    background: #262626!important;
}

.event-content {
    background: #3D3D3D!important;  
}
.event-add-button  {
    background-color: #bc2d2c !important;
    border-top-color: #C13B3A !important;
    border-bottom-color:#A92928 !important;
}


/** Betslip **/
#betslip-outer {
    background: #3D3D3D!important;  
    border: 1px solid #5a5e6d !important;
    border-radius: 0px!important;
    box-shadow: none!important;
}
#betslip-header {
    border-radius: 0px!important;
    background: #262626!important;
}

#betslip-header #betslip-title, #betslip-header .betslip-header-icon {
    color: #fff!important;
}
#betslip-text {
    color: #dcdcdc!important;
}
.betslip-error {
    border-radius: 0px!important;     
}
#betslip-label {
    background: #ffb129!important;
    color: #262626!important;
}
.tiny-blue-button {
    background-color: #bc2d2c !important;
    border-top-color: #C13B3A !important;
    border-bottom-color:#A92928 !important;
}
#betslip-type-buttons {
    border-top: none!important;
}
.betslip-type-button {
    width: 33.33%!important;
    border-top: none!important;
    border-bottom: none!important;
    background-color: #666!important;
    color: #ffb129 !important;
}
.betslip-type-button-active {
    background-color: #bc2d2c !important;
    color: #fff!important;
}
.betslip-type-button-disabled {
    color: #dcdcdc!important;    
}

.betslip-row {
    background: #262626!important;
    border-bottom: 2px solid #5a5e6d!important;
}
.betslip-row:last-of-type {
    border-bottom: none!important;
}
.betslip-row-event-text {
    color: #dcdcdc!important;
}
.betslip-row-bet-text {
    color: #fff!important;
}
.betslip-row-textbox-label {
    color: #dcdcdc!important;
}
#betslip-parlay-area, #betslip-teaser-area {
    margin-top: 0!important;
    border-top: 2px solid #5a5e6d!important;
}
#parlay-info, #teaser-info {
    background: #262626!important;
    border-radius: 0!important;
}

/** Wager **/
.mywager-outer2 {
    padding: 0!important;
    background: #262626!important;
    border: 1px solid #5a5e6d !important;
    border-radius: 0!important;
    box-shadow: none!important;
}
.mywager-header2 {
    padding: 10px 15px;
    background: #262626!important;
    border-bottom: 1px solid #5a5e6d !important;
}
.mywager-table {
    border-top: none!important;
    background: #3D3D3D!important
}
.mywager-table-row-pending, .mywager-table-row-cancel {
    background: none!important;
}
.mywager-table-row-lose {
    background: #962423!important;
}
.mywager-table-row-win {
    background: #7cad72!important;
}
.mywager-table-header2, .mywager-parlay-row {
    padding: 0 10px;
    box-sizing: border-box;
    background: #262626!important;
    border-bottom: 1px solid #5a5e6d !important;
    
    text-shadow: none!important;
    color: #fff!important;
}
.mywager-table-row2 {
    padding: 0 10px;
    box-sizing: border-box;
}
.mywager-table-row2:last-of-type {
    border-bottom: none!important;
}
.popover-content {
    color: #000!important;
}
.mywager-parlay-row {
    font-size: 12px!important;
}
}
