css:
@-moz-document url-prefix("https://i.hero-wars-fb.com/iframe_social/facebook/heroes") {
.bg--heroes #flash-wrapper:after, .bg--heroes #flash-wrapper:before {
    width: 0;
    height: 0;
}

.bg--heroes-fb #flash-content {
    width: 100vw;
    height: calc(100vh)!important;
	position:fixed;
    top: 0;
    left: 0;
}
}

@-moz-document url-prefix("https://apps.facebook.com/mobaheroes") {
/* 
Mar 21: ditched urls - 
https://i-heroes-fb.nextersglobal.com/
https://heroes-a.akamaihd.net/iframe_v2/
https://heroes-a.akamaihd.net/fb
*/

body {
    overflow: hidden!important;
}
#mainContainer {
	position: fixed;
	top: 0;
	z-index: 350;
}
#rightCol, .bg--heroes .customFooterContent:after, .bg--heroes .customFooterContent:before {
    display: none;
}
#contentCol, #contentArea, .canvas_main_content, #iframe_canvas {
    width: 100vw;
    height: 100vh;
}
}
