css:
/* 
Script developed by Diego Arndt
Last updated on 05.05.2020
Version 1.0
*/
@-moz-document domain("globoesporte.globo.com")
{
html {
    background: #181818 !important;
}

::-webkit-scrollbar {
    width: 7px;
}

::-webkit-scrollbar-track {
    background: #181818;
}

::-webkit-scrollbar-thumb {
    background: #888;
}

::-webkit-scrollbar-thumb:hover {
    background: #555;
}

/*----- DEFAULT TEXT, BORDER & BACKGROUND COLORS -----*/
* {
    box-shadow: none !important;
    background-color: transparent !important;
    border-color: #555 !important;
}

body {
    background: transparent !important;
}

a,
a * {
    text-decoration: none !important;
}

a:hover,
a:hover *,
a:visited:hover,
a:visited:hover *,
span[onclick]:hover,
div[onclick]:hover,
[role="link"]:hover,
[role="link"]:hover *,
[role="button"]:hover *,
[role="menuitem"]:hover,
[role="menuitem"]:hover *,
.link:hover,
.link:hover * {
    color: #F0F0F0 !important;
    text-shadow: 0 0 2px #8a8a8ae6 !important;
}

a.highlight,
a.highlight *,
a.active,
a.active *,
.selected,
.selected *,
[href="#"] {
    color: #DDD !important;
    font-weight: bold !important;
}

p,
h1,
h2,
h3,
h4,
h5,
h6,
h1 *,
h2 *,
h3 *,
strong,
[id*="headline"],
[class*="headline"],
[id*="header"],
[class*="header"],
[class*="header"] td {
    color: #F0F0F0 !important;
}

a h1,
a h2,
a h3,
a h4,
a h5,
a h6,
h1 a,
h2 a,
h3 a,
a strong,
a[id*="headline"],
a[class*="headline"],
a[id*="header"],
a[class*="header"] {
    text-decoration: underline !important;
}

::-moz-selection {
    background: #3b9c00 !important;
}

::selection {
    background: #3b9c00 !important;
}

:focus {
    outline: none !important;
}

/*----- MENU & CO BACKGROUND-COLORS -----*/
table {
    background: rgba(30, 30, 40, .6) !important;
    border-radius: 6px !important;
}

table > tbody > tr:nth-child(even),
table > tbody > tr > td:nth-child(even) {
    background-color: rgba(0, 0, 0, .2) !important;
}

header,
#header,
footer,
#footer {
    background: rgba(19, 19, 19, .9) !important;
    box-shadow: 0 0 5px #000 !important;
}

body > #dialog,
body > .xenOverlay {
    background: rgba(19, 19, 19, .96) !important;
    background-clip: padding-box !important;
    box-shadow: 0 0 15px #000, inset 0 0 0 1px rgba(200, 200, 200, .5), inset 0 0 5px #111 !important;
    border: 10px solid rgba(99, 99, 99, .7) !important;
    border-radius: 0 !important;
}

[id*="overlay"],
[id*="lightbox"],
blockquote {
    background-color: rgba(35, 35, 35, .9) !important;
    border-radius: 5px;
}

pre,
dl,
.Message code {
    background-color: rgba(5, 5, 5, .5) !important;
}

/*----- DEFAULT BUTTONS, SEARCHBOXES & CO -----*/
input,
select,
button,
[role="button"],
a.button,
a.submit,
a.BigButton,
a.TabLink,
.install[onclick] {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    transition: border-color 0.3s !important;
    color: #BBB !important;
    text-shadow: 0 1px #000 !important;
    border: 2px solid #333 !important;
    border-radius: 4px !important;
    box-shadow: 0 0 2px rgba(0, 0, 0, .9) !important;
}

a[href="javascript:;"]:hover,
a[class*="button"]:not(:empty):hover,
a[id*="button"]:hover,
a[id*="Button"]:not(:empty):hover,
div[class*="button"][onclick]:hover {
    background: #181818 !important;
    color: #FFF !important;
}

input *,
select *,
button *,
a.button *,
a.submit * {
    color: #BBB !important;
    text-shadow: none !important;
}

input *:hover * {
    color: #F0F0F0 !important;
    text-shadow: 0 0 2px #FFF !important;
}

input[type="checkbox"]:checked,
input[type="radio"]:checked {
    border-color: #3b9c00 !important;
    box-shadow: 0 0 5px #3b9c00 !important;
}

select {
    padding-right: 15px !important;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAECAYAAABCxiV9AAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAeSURBVBhXY5g5c+Z/BiwALo6uAEMDTACXSWgSDAwA4jATh950E30AAAAASUVORK5CYII=) no-repeat right 4px center #060606 !important;
    transition: border-color 0.3s, background-position 0.3s !important;
}

button:active,
input[type="submit"]:active,
input[type="button"]:active,
a.button:active,
a[class*="button"]:not(:empty):active,
a.submit:active,
a.BigButton:active,
a.TabLink:active,
.Active .TabLink {
    background: #292929 !important;
    color: #FFF !important;
}

/*----- IMAGE CHANGES -----*/
body,
*:not(:empty):not(html):not(span):not(a):not(b):not(option):not(select):not(img):not([style="display: block;"]):not([onclick*="open"]):not([onclick*="s_objectID"]):not([class*="stars"]):not([id*="stars"]):not([id="rating"]):not([class="rating"]):not([class*="SPRITE"]):not([id*="SPRITE"]):not([class*="item"]):not([id*="item"]):not([class*="thumb"]):not([class*="icon"]):not([class*="photo"]):not(.view):not(.text):not([id*="lbImage"]):not([class*="cc-in"]):not([class*="gr-body"]):not([id*="watch"]):not(#globalsearch),
.r3_hm,
.gmbutton2 b,
.gtab-i,
.ph,
.bstab-iLft,
.csb,
#pagination div,
[style*="sprite2.png"],
#mw-head-base,
#mw-page-base {
    background-image: none !important;
}

img {
    opacity: .7 !important;
    transition: opacity .2s;
}

img:hover,
a:hover img,
#mpiv-popup {
    opacity: 1 !important;
}

/*----- ADITIONAL CHANGES -----*/
[class*="nav"] ul {
    background: none !important;
    box-shadow: none !important;
}

[class*="mosaico__equipes"],
[class*="floating-bar"],
[class*="menu-container"] {
    background-color: #202020 !important;
}

[title="Juventus"] [class*="mosaico__escudo"] {
    background-color: #ffffff87 !important;
}

[class*="glb-grid grid-24 lingueta-premiere"],
[class="row shopping"],
[class="diretorio__audience"],
[class="publicidade publicidade-banner_slb_topo"],
[class="publicidade publicidade-banner_slb_meio"],
[class="bstn-feed-ad"] {
    display: none;
}

[class="bstn-item-shape"] {
    margin: auto !important;
}

[class*="mosaico__header-personalization--seta"],
[class*="button"] {
    border: none !important;
}

[class="bstn-hl-video gui-lightbox-open"] a,
a *, li, div {
    color: #DDD !important;
}

a[class*="header"] {
    text-decoration: none !important;
}

[class*="tabela__pontos"] {
    overflow: hidden !important;
}

[id*="boxComentarios"] {
    background-color: #DDD !important;
    padding: 5px;
}

.theme a {
    color: #3e831d !important;
}

.footer__copyright:after {
    content: " | Dark theme developed by Diego Arndt";
}
}
