:focus {
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
}

.feature_pages #on_this_page_column {
	position: relative;
}

.side-bar.is-open {
	overflow: hidden;
}

#similar {
    float: right;
}

/*___ Fonts ________________________________________________________________________*/

body {
	font-family: Helvetica, Arial, sans-serif;
}

.feature_pages .numbered_grid_module .numbered_grid_title,
body,
p {
	font-size: 21px!important;
}

/*___ Remove Items _________________________________________________________________*/

.edu-feed__submenu-topics--list li.first a:after,
.sitewide-banner,
.snippet-card .content::after,
.submenu-topics__list li.first a:after,
.tooltip-tip,
.wasnt_it_good,
[src="ground.en.png"]
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.feature_pages .wysiwyg_content .image_module.right,
.feature_pages .wysiwyg_content .related_content_module.right,
.feature_pages .wysiwyg_content .video_player_container.right,
.feature_pages .wysiwyg_content figure.embedded_video.right {
	margin-right: 0;
}

#search-bar {
    margin-right: 4rem;
}

.feature_pages .column-width .image_carousel .ms-nav-prev {
    left: -32px;
}

.feature_pages .column-width .image_carousel .ms-nav-next {
    right: -33px;
}

/*___ Margins ______________________________________________________________________*/

#full-view,
#content,
#content-wrapper,
.collage_teaser_module,
.feature_pages #primary_column,
.menuFeatured .featuredElement,
.side-bar,
.suggested_features.module,
.wysiwyg_content .teaser_grid_item {
	margin: 12px!important;
}

.feature_pages .wysiwyg_content .related_content_module.right {
	margin: 11em 0 1.5em 2.5em;
}

#page-header,
#main {
	margin: 19px auto !important;
}

#full-view {
	margin: 5rem 0!important;
}

#container {
    margin: 0;
}

/*___ Paddings _____________________________________________________________________*/

#full-view,
#content,
.feature_pages #primary_column,
.grid_gallery.grid_view li.slide,
.grid_layout,
.menuFeatured .featuredElement,
.tooltip,
.wysiwyg_content .teaser_grid_item,
blockquote,
header,
section.single_teaser.module
{
	padding: 12px!important;
}

.wysiwyg_content .teaser_grid.layout_c {
	display: grid;
	padding-left: 0.875em;
	padding-right: 6.125em;
}



/*___ Height _______________________________________________________________________*/

.headline {
    max-height: none;
}

#full-view {
    height: 89%;
}

#menu ul li {
    height: 7rem;
}

/*___ Widths _______________________________________________________________________*/

#navbar-nasa,
#full-view,
.feature_pages .wysiwyg_content>*,
.grid_layout,
.tv-player-pane .player-wrapper,
.tv-schedule,
.wysiwyg_content .teaser_grid.layout_c {
	max-width: none!important;
}

#container,
header {
	width: 100%;
}

#menu,
#page-header,
#main,
.feature_pages #primary_column,
.feature_pages .wysiwyg_content>*,
.grid_layout,
.infopane {
	width: 98%!important;
}

[src*="/internal_resources/"] {
	width: 50%;
}

#full-view {
	width: 96%!important;
}












