css:
@-moz-document domain('blog.csdn.net') {
  #csdn-toolbar, #rightAside, .operating, .c-gray, .un-collection, .read-count, .article-read-img, .article-type-img, .hide-article-box, .blog-expert-recommend-box, .recommend-item-box.type_hot_word, .recommend-ad-box, .isGreatIcon, .tool-box, .meau-gotop-box, .recommend-end-box, .login-mark, .blog_title_box.oneline, .recommend-item-box::before, .pulllog-box, #mainBox > aside, .recommend-box, .template-box, .more-toolbox, .comment-box, .right-message, .main-login, .identity-icon, .show-txt, .title .flag, #passportbox, .column-advert-box, .article-plan-img, .article-plan-text, .up-time, .article-vip-img, .article-vip-text {
    display: none!important;
}
body {
    font-family: source-han-serif-tc, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif!important;
    min-width: unset;
    background: none!important;
    background-color: rgba(10, 10, 10, 0.8)!important;
}
.h1, .h2, .h3, .h4, .h5, .h6, a, abbr, body, cite, dd, dl, dt, h1, h2, h3, h4, h5, h6, iframe, input, li, object, ol, p, pre, span, ul {
    font-family: source-han-serif-tc, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif!important;
}
code, pre, code span, pre span {
    font-family:"Source Code Pro", Monaco, Menlo, Consolas, monospace!important;
}
#article_content {
    height: auto!important;
}
#mainBox {
    margin-left: auto;
    margin-right: auto;
    width: 60%;
}
#mainBox > main {
    display: block!important;
    float: none;
    width: 100%;
}
.recommend-item-box {
    width: 50%;
    max-width: 25rem;
}
.recommend-item-box .content, .recommend-item-box h4 {
    width: 100%!important;
}
.main_father {
    padding: 80px;
}
a[data-type='cs'], a[data-type='report'], a[data-type='app'] {
    display: none!important;
}
.blog-content-box {
    border-radius: 10px!important;
    padding: 80px 80px 40px 80px!important;
}
.title-article {
    color: #222222!important;
}
h1 {
    font-size: 20px!important;
}
h2 {
    font-size: 18px!important;
}
h3, h4, h5, h6 {
    font-size: 18px!important;
}
.hljs-button.signin {
    display: none!important;
}
#content_views a, .follow-nickName {
    color: #42b983!important;
}
code {
    background-color: #f8f8f8!important;
    color: #e96900!important;
}
.pre-numbering {
    font-size: 12px!important;
}
.markdown_views blockquote p {
    color: #6e6e6e!important;
}
blockquote {
    border-left: 6px solid #42b983!important;
}
.markdown_views.prism-atom-one-dark .prettyprint, .markdown_views.prism-atom-one-dark .prettyprint .pre-numbering, .markdown_views.prism-atom-one-dark pre.prettyprint {
    background-color:#f6f8fa!important;
}
div.article-title-box > h1 {
    font-size:32px!important;
}
.csdn-side-toolbar {
    margin: 40px!important;
}
.article-info-box, .imgViewDom {
    background-color: rgba(0, 0, 0, 0)!important;
}
}
