@-moz-document regexp(".*\\:8161.*") {
#site-breadcrumbs a {
  display: inline-block !important;
  *display: inline!important;
  zoom: 1!important;
  padding: 6px 20px!important;
  margin: 0!important;
  cursor: pointer!important;
  border: 1px solid #bbb!important;
  overflow: visible!important;
  font: bold 13px arial, helvetica, sans-serif!important;
  text-decoration: none!important;
  white-space: nowrap!important;
  color: #555!important;
  
  background-color: #ddd!important;
  background-image: -webkit-gradient(linear, left top, left bottom, from(rgba(255,255,255,1)), to(rgba(255,255,255,0)))!important;
  background-image: -webkit-linear-gradient(top, rgba(255,255,255,1), rgba(255,255,255,0))!important;
  background-image: -moz-linear-gradient(top, rgba(255,255,255,1), rgba(255,255,255,0))!important;
  background-image: -ms-linear-gradient(top, rgba(255,255,255,1), rgba(255,255,255,0))!important;
  background-image: -o-linear-gradient(top, rgba(255,255,255,1), rgba(255,255,255,0))!important;
  background-image: linear-gradient(top, rgba(255,255,255,1), rgba(255,255,255,0))!important;
  
  -webkit-transition: background-color .2s ease-out!important;
  -moz-transition: background-color .2s ease-out!important;
  -ms-transition: background-color .2s ease-out!important;
  -o-transition: background-color .2s ease-out!important;
  transition: background-color .2s ease-out!important;
  background-clip: padding-box!important; /* Fix bleeding */
  -moz-border-radius: 3px!important;
  -webkit-border-radius: 3px!important;
  border-radius: 3px!important;
  -moz-box-shadow: 0 1px 0 rgba(0, 0, 0, .3), 0 2px 2px -1px rgba(0, 0, 0, .5), 0 1px 0 rgba(255, 255, 255, .3) inset!important;
  -webkit-box-shadow: 0 1px 0 rgba(0, 0, 0, .3), 0 2px 2px -1px rgba(0, 0, 0, .5), 0 1px 0 rgba(255, 255, 255, .3) inset!important;
  box-shadow: 0 1px 0 rgba(0, 0, 0, .3), 0 2px 2px -1px rgba(0, 0, 0, .5), 0 1px 0 rgba(255, 255, 255, .3) inset!important;
  text-shadow: 0 1px 0 rgba(255,255,255, .9)!important;
  
  -webkit-touch-callout: none!important;
  -webkit-user-select: none!important;
  -khtml-user-select: none!important;
  -moz-user-select: none!important;
  -ms-user-select: none!important;
  user-select: none!important;
}

#site-breadcrumbs a:hover{
  background-color: #eee!important;
  color: #555;
}

#site-breadcrumbs a:active{
  background: #e9e9e9;
  position: relative;
  top: 1px;
  text-shadow: none;
  -moz-box-shadow: 0 1px 1px rgba(0, 0, 0, .3) inset;
  -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, .3) inset;
  box-shadow: 0 1px 1px rgba(0, 0, 0, .3) inset;
}

.button[disabled], .button[disabled]:hover, .button[disabled]:active{
  border-color: #eaeaea;
  background: #fafafa;
  cursor: default;
  position: static;
  color: #999;
  /* Usually, !important should be avoided but here it's really needed :) */
  -moz-box-shadow: none !important;
  -webkit-box-shadow: none !important;
  box-shadow: none !important;
  text-shadow: none !important;
}

div#site-breadcrumbs {
    color: transparent;
    padding: 5px;
}
div.top_red_bar {
    background: #fff;
    padding: 5px 0;
    border-top: 1px solid #eee;
    border-bottom: 1px solid #eee;
    margin-bottom: 20px;
}
div.black_box div.footer_r {
    background: #1e548a;
}
div.black_box div.footer_l {
    background: #1e548a;
}
div.black_box {
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
    overflow: hidden;
    -webkit-box-shadow: 0px 9px 15px -12px rgba(0, 0, 0, 0.65);
    -moz-box-shadow: 0px 9px 15px -12px rgba(0, 0, 0, 0.65);
    box-shadow: 0px 9px 15px -12px rgba(0, 0, 0, 0.65);
}
div.white_box div.header {
    background: #1e548a;
    overflow: hidden;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
div.white_box div.header_r {
    background: transparent;
}
div.white_box div.header_l {
    background: transparent;
}
div.white_box div.content_r {
    background: transparent;
}
div.white_box div.content_l {
    background: transparent;
}
div.white_box div.content {
    background: #ffffff;
    padding: 1rem;
}
div#activemq_logo {
    background: url(https://activemq.apache.org/components/artemis/documentation/2.4.0/images/artemis-logo.png) no-repeat top left;
    background-size: 227px 72px;
    color: transparent;
}
div#activemq_logo a {
    display: none !important;
}
body {
    background-color: #3c78b5;
}
div.navigation {
    display: none;
}
* {
    box-sizing: border-box;
}
table {
    border-spacing: 0px;
    border-collapse: collapse;
    width: 100%;
    max-width: 100%;
    margin-bottom: 15px;
    background-color: transparent;
    /* Change the background-color of table here */
    text-align: left;
    /* Change the text-alignment of table here */
}
th {
    font-weight: bold;
    border: 1px solid #cccccc;
    /* Change the border-color of heading here */
    padding: 8px;
}
td {
    border: 1px solid #cccccc;
    color: #888;
    /* Change the border-color of cells here */
    padding: 8px;
    font-size: 12px;
}
/* Stylized */

/* Adding Striped Effect for odd rows */
 tr {
    background-color: transparent;
    /* Change the default background-color of rows here */
}
tr:nth-of-type(2n+1) {
    background-color: #eeeeee;
    /* Change the background-color of odd rows here */
}
tr th {
    background-color: #ddd;
    color: #fff;
    /* Change the background-color of heading here */
}
/* Adding Hover Effect for rows */
 th, td {
    border: none;
    font-size: 12px;
}

td {
	background-color: #fff;
}

a:link, a:visited, a:active, a:hover {
    color: #3988e5;
    text-decoration: none;
}

div.bottom_red_bar {
    background: #fff;
    height: 11px;
    overflow: hidden;
}
}
