css:
@-moz-document domain("kanbanflow.com") {

.body--largeTaskName .task-name {
    font-size: 25px;
}
.board-taskListCell {
    background: url("https://upload.wikimedia.org/wikipedia/commons/7/7f/Kaohsiung_harbor_sunrise_skyline.jpg");
    background-attachment: fixed;
    background-size: cover;
    background-position: center center;
}
.board-taskListCell.column-first {
    border-bottom-left-radius: 10px;
}
.board-taskListCell.column-last {
    border-bottom-right-radius: 10px;
}

.board-taskListCell {
    border: none;
}
.boardMenu-header {
    background: #ebecf0 !important;
    border: none;
}
.boardMenu-getPremium {
    display: none;
}
.boardMenu-content {
    background: white;
}

.taskList, .columnHeader {
    height: auto;
    margin: 10px;
    bottom: auto;
    background-color: #ebecf0 !important;
    border: none;
}
.columnHeader-addTask {
    transition: 0.25s ease background;
}
.columnHeader-addTask:after,.columnHeader-addTask:before {
    transition: 0.25s ease background-color;
}


.board-colorLegendItem {
    margin: 5px;
    border-radius: 5px;
    border-top-width: 5px;
}


.task {
    background-color: white !important;
    border-color: white !important;
    border: none;
}
.task, .taskList {
    font-size: 18px;
    box-shadow: 0 1px 0 rgba(9,30,66,.25);
    border-radius: 5px;
    padding-top: 15px;
    padding-bottom: 15px;
    padding-left: 5px;
    padding-right: 5px;
}
.task:before {
    content: " ";
    display: block;
    width: 60px;
    height: 12px;
    background: red;
    border-radius: 6px;
}

.task.task-yellow:before {
    background: #FFEE58;
}
.task-yellow .subTasks,
.task-yellow .task-dueDates {
    border-top: 1px solid #FFEE58;
}
.board-colorLegend-yellow {
    border-color: #FFEE58;
    background-color: #FFF9C4;
}
.addTaskDialog-colorIcon.addTaskDialog-colorIcon--yellow {
    background: #FFEE58;
}
.taskBorderColor--yellow {
    border-color: #FFEE58;
}
.taskColor--yellow {
    background: #FFF9C4;
}

