css:
/* MangaHost D E L U X E | Lucas Celli */

@-moz-document domain(mangahost2.com){
body {
    font-family:'Segoe UI';
}
.ads, .ads970x90, .ads300x600, .ads728x90, .ads336x280, .ads300x250, .ads-content {
    opacity: 0;
    height: 0;
    width: 0;
    visibility: hidden;
    display: none;
}
.sidebar {
    margin-top: 7px;
}
.title-widget {
    background: #607D8B;
    padding:10px;
    margin: 0;
}
.list-featured {
    margin-top: 10px;
}
h1, h2, .bebas {
    font-family:'Segoe UI';
}
.title-widget b {
    display: none;
}
.title-widget h2, .title-widget h1 {
    background: #607D8B;
    font-weight: 100;
    color: #fff;
}
.navbar-inverse .navbar-inner {
    background: #263238;
    padding-bottom: 5px;
}
.list-featured .thumbnail, .table, .well2, .nav-mangas {
    border: 1px solid #607D8B;
}
.table td, .table th {
    border-top: 1px solid #607D8B;
}
.nav-mangas li {
    border-bottom: 1px solid #607D8B;
}
.nav-mangas a {
    margin-right: 10px;
}
.label-info, .badge-info {
    background: #607D8B;
}
.label-info:hover, .label {
    background: #607D8B;
}
.label {
    border-radius: 0
}
.table-lancamentos .chapters a.btn-mini {
    background: #607D8B;
    border: none;
    border-radius: 0;
    color: #fff;
    text-shadow: none;
}
.table tr td a.btn:visited {
    background: #78909C;
}
.entry-title {
    color: #607D8B;
    font-weight: 100;
    text-transform: uppercase;
}
a {
    color: #607D8B;
}
.navbar .navbar-search input, .navbar .navbar-search select {
    border: none;
    background: #78909C;
    border-radius: 0;
    padding: 5px;
    margin-top: 5px;
}
.navbar .nav{
    top: 5px;
    left: 30px;
}
.btn{
    border: none;
    box-shadow: none;
}
.btn-info {
    border: none;
    background: #607D8B;
}
.navbar .navbar-search .btn-info {
    border-radius: 0;
    line-height: 150%;
    margin-top: 5px;
}
.navbar-inner .container:after {
    content:"D E L U X E | by Lucas Celli";
    color: #78909C;
    font-size: 80%;
}
.btn, .viewerChapter, .viewerPage{
    border-radius: 0;
}
.navbar .login{
    margin-top: 5px;
}
div.bbp-template-notice:after{
    content: "MangaHost DELUXE foi criado por Lucas Celli, obrigado por utilizar esse tema!";
    display: inline-block;
    background: #607D8B;
    padding: 10px;
}
div.bbp-template-notice.important{
    color: #fff;
    border: none;
    background-color: #424242;
}
div.bbp-template-notice a{
    color: #607D8B;
}
}
