css:
@-moz-document domain("omorashi.org"), domain("www.omorashi.org") {
.ipsWidget.ipsWidget_vertical .ipsWidget_title {
    padding: 10px;
    margin: 0;
    font-size: 12px;
    font-weight: 500;
    position: relative;
    color: #e6e8ea;
    background: #202225;
    border-radius: 0;
    font-family: open\ sans;
    border: 1px solid #e0e0e00d;
}
.ipsApp .ipsButton_light {
    background: #5e5e5e;
    border: 1px solid #e0e0e008;
    color: #e6e8ea;
}
.ipsTabs {
    background: #202225;
    padding: 10px 10px 0;
    margin: 0;
    list-style: none;
}
#elSearch {
    border-radius: 0;
    padding: 4px 10px;
    background: #26292d;
    border: 3px solid #2e3135;
    margin: 0;
    height: 34px;
    width: 270px;
    position: relative;
    z-index: 1;
}
.ipsSideMenu_itemActive a, a.ipsSideMenu_itemActive, span.ipsSideMenu_itemActive, .ipsSideMenu_withChecks .ipsSideMenu_item>input[type=checkbox]:checked~a, .ipsSideMenu_withChecks .ipsSideMenu_item>input[type=checkbox]:checked~span, .ipsSideMenu_withRadios .ipsSideMenu_item>input[type=radio]:checked~a, .ipsSideMenu_withRadios .ipsSideMenu_item>input[type=radio]:checked~span {
    background: #272727;
    color: #e6e8ea;
}
.usermenu {
    padding: 22px;
    background: #202225;
    margin-top: -8.5em;
    border-left: 2px solid #26292d;
    border-right: 2px solid #26292d;
    border-top: 2px solid #26292d;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
}
.menuott .fa {
    font-size: 12px;
    color: #cfccc3;
    background: #2e3135;
    height: 50px;
    line-height: 50px;
    width: 30px;
    text-align: center;
    position: relative;
    top: 1px;
    transition: all .4s ease;
}
.activefaz {
    font-size: 10px;
    color: #e1e1e1!important;
    background: #2e3135!important;
    height: 50px;
    line-height: 50px;
    width: 50px!important;
    text-align: center;
    position: relative;
    top: 1px;
    transition: all .4s ease;
}
.ipsType_sectionTitle {
    font-size: 13px;
    font-weight: 500;
    padding: 14px;
    margin-top: 0;
    background: #2e3135;
    color: #fff;
    margin: 4px!important;
    border-radius: 0;
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
}
.ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    padding: 10px!important;
    margin: 0!important;
    font-size: 12px;
    font-weight: 500;
    position: relative;
    color: #e6e8ea;
    background: #2e3135!important;
    border-radius: 0!important;
    font-family: open\ sans;
    border: 1px solid #e0e0e00d;
}
.ipsItemStatus.ipsItemStatus_large {
    background: #373a3e;
    color: #fff;
    width: 28px;
    border: 3px solid #2e3135;
    height: 28px;
    line-height: 22px;
    border-radius: 4px;
    display: inline-block;
    text-align: center;
    font-size: 18px;
    font-family: kanit,sans-serif;
    transition: .15s all linear;
}
.ipsApp .ipsButton_important {
    background: #424242;
    color: #e6e8ea;
}
.imgsrcz img {
    padding: 4px;
    border: 2px solid #2e3135;
}
.sepa {
    display: inline-block;
    height: 18px;
    width: 4px;
    border-top-left-radius: 25px;
    border-bottom-right-radius: 25px;
    background: #2e3135;
    opacity: 1;
    position: relative;
    margin-left: 14px;
    margin-right: 14px;
    top: 6px;
    transform: rotate(20deg);
}
.headerth {
    position: relative;
    padding: 22px;
    height: 243px;
    line-height: 243px;
    background: #202225 url(https://iili.io/JlaqJf.jpg);
    background-size: cover;
    background-position: 10% 70%;
    border-bottom: 2px solid #26292d;
}
.ipsButtonBar .ipsPagination .ipsPagination_page.ipsPagination_active a {
    background: #868686;
    border-color: #414141;
    color: #e6e8ea;
}
.ipsButtonBar .ipsButtonRow li>a, .ipsButtonBar .ipsButtonRow li>button, .ipsButtonBar .ipsButtonRow li>span {
    background: #3c3c3c;
    border: 1px solid #e0e0e008;
    color: #e6e8ea;
    font-weight: 400;
}
.ipsFollow .ipsCommentCount {
    margin: 0 3px 3px 0;
    background: #3c3c3c;
    color: #e6e8ea;
    text-shadow: none!important;
    line-height: 11.5px;
    font-size: 10px;
}
.chvr {
    font-size: 9px!important;
    color: #757575;
}
a[data-mentionid] {
    display: inline-block;
    border-radius: 2px;
    padding: 0 5px;
    font-size: 90%;
    color: #fff;
    text-decoration: none!important;
    background: #828282;
}
.cMultiQuote {
    border: 3px solid #484848!important;
}
.ipsTags .ipsTag, .ipsTag_prefix {
    background: #474747;
    color: #fff!important;
    font-size: 10px;
    line-height: 18px;
    height: 18px;
    padding: 0 7px;
    margin: 0 3px 0 8px;
    display: block;
    position: relative;
    letter-spacing: 0;
    border-radius: 0 4px 4px 0;
}
.ipsItemStatus:not( .ipsItemStatus_large ) {
    color: #a05664;
    font-size: 14px;
    line-height: inherit;
    vertical-align: middle;
}
.right-ups .fa {
    background: #5e5e5e;
    color: #e6e8ea;
    height: 20px;
    font-size: 10px;
    line-height: 20px;
    text-align: center;
    width: 20px;
    margin-right: -4px;
    margin-left: 4px;
    border-radius: 2px;
}
.cProfileRepScore_positive {
    background: #ccb476;
    color: #fff;
}
.ipsApp .ipsNotificationCount {
    position: absolute;
    top: -17px;
    font-size: 11px;
    display: inline-block;
    text-indent: 0%;
    line-height: 14px;
    padding: 0 6px;
    border-radius: 4px;
    z-index: 2;
    color: #e6e8ea;
    background: #545454;
    border: 2px solid #434343;
}
.ipsPagination .ipsPagination_page.ipsPagination_active a {
    background: #4e4e4e;
    border-color: #262626;
    color: #e6e8ea;
    box-shadow: none;
    text-shadow: none;
}
.cForumRow_hidden .ipsType_sectionTitle {
    background: #444444;
}
















































}
