css:
@-moz-document url-prefix("https://flightrising.com/main.php?p=hoard"), url-prefix("https://flightrising.com/main.php?p=vault") {
/* Bigger Checkbox accessability */
.common-pagination {
    margin-bottom: 0px !important
}
#super-container {
    width: 95% !important;
}
#inv_main {
    width: 100% !important;
    height: auto !important;
    background-size: 982px 567px
}

#invent > div:nth-child(32),
#invent > div:nth-child(31) {
    position: relative !important
}
input[type="checkbox"] {
    height: 50px;
    width: 50px;
    margin-top: 0px
}
select {
    width: 50px;
    height: 30px;
    font-size: 16px;
    margin-top: 0px
}
select > option {
    font-size: 16px
}
#invent > span {
    height: auto !important;
    border-bottom: 2px solid #7d1b1b;
    margin-bottom: 2px
}
}
