css:
@-moz-document regexp(".*olx.*.") {

body {
    color: #30ff2e;
    background: black;
}
ul.tabs .tab .link,
ul.tabs .tab a {
    color: white;
}
table.offers.redesigned .offer-wrapper,
table.offers.redesigned.userobserved-list .offer-wrapper,
listHandler table.offers.redesigned .offer-wrapper {
    background: #262626;
}
form.search .checkboxsepa .small {
    font-size: 14px !important;
    line-height: 1.29;
    color: #4fbf46;
}
form.search .checkboxsepa .icon.f_checkbox.selected + .small {
    color: #30ff2e;
}
.link {
    color: #30ff2e;
}
form.search .option-search-criteria a {
    -webkit-box-shadow: inset 0 0 0 2px #378436;
    -moz-box-shadow: inset 0 0 0 2px #378436;
    -ms-box-shadow: inset 0 0 0 2px #378436;
    -o-box-shadow: inset 0 0 0 2px #378436;
    box-shadow: inset 0 0 0 2px #378436;
}
a {
    color: #10d9ee;
}
ul.tabs .tab.selected {
    color: #30ff2e;
    box-shadow: inset 0 -2px 0 0 #30ff2e, 0 1px 0 0 #22ae20;
}
.breadcrumb.offerslist .small {
    color: #30ff2e;
}
.view-lists > ul > li {
    color: #30ff2e;
}
.view-lists .view__icon {
    color: #007784 !important;
}
.offersview div.section h2 {
    color: #00a1b3;
}
table.offers.redesigned .offer .bottom-cell .breadcrumb span,
table.offers.redesigned.userobserved-list .offer .bottom-cell .breadcrumb span,
listHandler table.offers.redesigned .offer .bottom-cell .breadcrumb span {
    color: #00a1b3;
}
.promo-label--courier {
    color: #65c1ca;
    background: #575757;
}
.offersview div.section p {
    color: #00a1b3;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #00a1b3;
}
.maincategories {
    background: #262626;
}
.gallerywide > li {
    background: #262626;
}
form.searchmain input[type="text"] {
    background: #262626;
}
form.searchmain .input-container {
    background: #262626;
    border-right: 1px solid #6f6f6f;
}
form.searchmain [data-icon="search"] {
    color: #00a1b3;
}
form.searchmain .button {
    background: #262626;
    color: #30ff2e;
}
.favgallerybox > li .card {
    background: #262626;
}
.popular-searches {
    background: #262626;
}
ul.tabsfav .tab {
    color: #30ff2e;
}
.c73 {
    color: #30ff2e;
}
#categoryLinksHeader .breaklist .link.gray,
#categoryLinksHeader .breaklist .link.gray > * {
    color: #30ff2e;
}
.offer__innerbox {
	background: #262626;
}
.descgallery {
	background: #262626;
}
.offer-sidebar__box {
	background: #262626;
}
.t-new_sidebar .quickcontact {
	background: #262626;
}
}
