css:
@-moz-document domain("dorzeczy.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #c81c3a #1c1c1c;
}
a,h1,h2,h3,h4{
    color: #c81c3a !important;
}
a:hover,p,span{
    color: #fff !important;
}
*{
    box-shadow: none !important;
	text-shadow: none !important;
}
input[type="text"],input[type="password"],input[type="email"],input[type="search"],input[type="submit"],input[type="tel"],input[type="url"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #c81c3a !important;
}
input[type="submit"]:hover{
    background: #c81c3a !important;
    color: #fff !important;
}
/*HEADER*/
.header-expand-bar span.close-expand-bar-button{
    background-color: #c81c3a;
}
.header-expand-bar span.close-expand-bar-button:hover{
    background-color: #c81c3a;
}
#header-search input{
    width: 87%;
}
.header-issue-large-bar a{
    color: #fff !important;
}
.header-issue-large-bar .button-read{
    background: #1c1c1c;
}
#issue-promo-2020-03-22-sticky, #issue-promo-2020-03-22-sticky .w span{
    color: #1c1c1c !important;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.section-main,.box-list li.box-list-item-color-light .news-open,.box-list li.box-list-item-color-white .news-open,.box-list li.box-list-item-issue-dorzeczy,.box-list li.box-list-item-issue-dorzeczy-historia{
    background: #1c1c1c;
}
#page-main .box-list .box-list-item.box-list-item-list.box-list-item-type-list-latest::after,.box-list li.box-list-item-color-light .news-titlelead-wrapper,.box-list li.box-list-item-color-white .news-titlelead-wrapper,.box-list li.box-list-item-issue .issue-art-list ul::after,.box-list li.box-list-item-color-white .news-containers .item-containers :first-child:not(.cntr-recomendeded)::before,.box-list li .news-containers .item-containers .cntr-21, .box-list li .news-containers .item-containers .cntr-21:hover, .box-list li .news-containers .item-containers .cntr-104, .box-list li .news-containers .item-containers .cntr-104:hover{
    background: transparent;
}
.box-list li,.page-box-header{
    background: #1c1c1c;
    border: 1px solid #c81c3a;
}
.box-list li.box-list-item-issue .issue-art-list ul{
    height: 469px;
}
.box-list li.size-2x2 .news-bookmark.news-bookmark-wwd,.box-list li .news-bookmark{
    color: #1c1c1c !important;
}
.box-list  li:hover .news-titlelead-wrapper a strong span{
    color: #c81c3a !important;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.art-content .share-buttons,.art-text-relation.art-text-relation-text .art-text-relation-inner a strong,.art-bar{
    background: #1c1c1c;
}
/*KOMENTARZE*/
.comments ul li div.comment-text{
    color: #fff;
}
.comments ul li header .comment-user{
    color: #c81c3a;
}
.comments ul li div.comment-text .comment-zoom{
    background: #c81c3ac2;
}
.comments ul li footer .comment-button.comment-vote-button.pointer,.comments ul li footer .comment-button{
    background-color: #1c1c1c;
}
.art-bar-comments .button,.arrows-expand-bar a,.sub-header{
    background: #1c1c1c;
    border: 1px solid #c81c3a;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*PODSTRONY*/
/*TYGODNIK*/
.section-issue,.art-text-relation.art-text-relation-issue-dorzeczy{
    background: #1c1c1c;
}
.section-issue .issue-options .issue-buttons a.button.button-dark{
    border: 1px solid #c81c3a;
}
.section-issue .issue-options .issue-buttons a.button-google-play i,.art-text-relation-issue-dorzeczy .issue-order nav a.button-google-play i,.section-issue .issue-options .issue-buttons a.button-app-store i,.art-text-relation-issue-dorzeczy .issue-order nav a.button-app-store i,.footer .issues .issue-buttons a.button-google-play i,.footer .issues .issue-buttons a.button-app-store i{
    display: block;
    font-size: 22px;
}
.art-text-relation-issue-dorzeczy .issue-order nav a.button-app-store.button,.art-text-relation-issue-dorzeczy .issue-order nav a.button-google-play.button,.footer .issues .issue-buttons a.button-app-store,.footer .issues .issue-buttons a.button-google-play{
    line-height: 40px;
}
/*TYGODNIK*/
/*GALERIE*/
.element-gallery, .element-gallery-info, .element-gallery figure li,.close-button{
    background-color: #1c1c1c !important;
}
.element-gallery header .element-gallery-header,.close-button:hover{
    background-color: #c81c3a !important;
}
.element-gallery-info .art-title{
    color: #c81c3a !important; 
}
.element-gallery header .element-gallery-header{
    box-shadow: #c81c3a 0 0 25px 20px !important;
}
.element-gallery figure li div.screen{
    box-shadow: none !important;
}
span.next span.next-button, span.prev span.prev-button{
    filter: invert(18%) sepia(38%) saturate(5810%) hue-rotate(335deg) brightness(97%) contrast(98%);
}
.box-list.box-list-simple li .news-image{
    height: 100%;
}
/*GALERIE*/
/*KONTAKT*/
.box-list.box-list-simple.box-list-simple-dates li, .box-list li.box-list-item.box-list-item-list li.box-list-item-type-row{
    border-bottom: 0;
}
#element-header .header-art-details{
    width: 285px;
}
/*KONTAKT*/
/*PODSTRONY*/
/*FOOTER*/
.footer .copyright:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #c81c3a;
    cursor: none;
}
/*FOOTER*/
}
