.completeness-gauge {
    position: relative;
    width: 95%;
    height: 20px;
    background-color: #909090a3;
}

.completeness-gauge > div {
    height: 100%;
    background-color: #b7b7b740;
}

.completeness-gauge > span {
    position: absolute;
    top: 55%;
    left: 50%;
    margin-right: -50%;
    transform: translate(-50%, -50%);
}

.toTop {
    position: fixed;
    padding: 20px;
    width: 100px;
    height: 100%;
    background-color: #f3f3f3;
    font-weight: 900;
    color: #2b2b2b;
    box-sizing: border-box;
    opacity: 0;
    transition: .1s all;
}

body.scrolled .toTop:hover {
    opacity: .5;
    cursor: pointer;
}


.ugc-table tr > td {
    word-break: break-word;
