css:
@-moz-document domain("lms.beuth-hochschule.de") {
:root {
    --link: #6dd;
    --border: #555;
    --background: #000;
    --foreground: #1a1a1a;
    --backgroundButton: #2a2a2a;
    --textColor: #fff;
    --calendarEventCourse: #402a26;
    --calendarEventSite: #2e3e28;
    --calendarEventCategory: #3f273f;
    --calendarEventGroup: #3d3729;
    --calendarEventUser: #243942;
}

a[href] {
    color: var(--link) !important;
    background: transparent !important;
    border-width: 0px !important;
}

#coc-hiddencoursescount {
    color: var(--link);
}

/* striped table in forum alternating color */
.table-striped > tbody > tr:nth-child(odd) > td,
.table-striped > tbody > tr:nth-child(odd) > th {
    background-color: var(--backgroundButton) !important;
}

.pt-3,
.col-12,
.bg-dark,
div[id=page-content],
body,
div#nav-drawer,
nav.list-group {
    color: var(--textColor) !important;
    background-color: var(--background) !important;
}

div.list-group-item,
span.media-body/**/
{
    background-color: var(--foreground) !important;
    border-color: var(--border) !important;
}

div.region_main_settings_menu_proxy {
    background: transparent!important;
    /* wtf? */
}

#prev-activity-link,
#next-activity-link {
    color: var(--textColor) !important;
    background-color: var(--backgroundButton) !important;
    border-color: var(--border) !important;
    border-radius: 2mm !important;
    border-width: 1px !important;
}

select {
    color: var(--textColor) !important;
    background-color: var(--backgroundButton) !important;
    border-color: var(--border) !important;
    border-radius: 2mm !important;
}

input {
    color: var(--textColor) !important;
    background-color: var(--foreground) !important;
    border-color: var(--border) !important;
    border-radius: 0mm !important;
}

i.icon {
    color: var(--textColor) !important;
}

/**/
div.card {
    background-color: transparent !important;
    border-color: var(--border) !important;
    border-radius: 1mm !important;
    border-width: 1px;
}

#region-main,
div.dropdown-divider,
div.card-body,
/**/
div.dropdown-menu {
    color: var(--textColor) !important;
    background-color: var(--foreground) !important;
    border-color: var(--border) !important;
    border-radius: 1mm !important;
}

textarea.form-control,
textarea {
    color: var(--textColor) !important;
    background-color: #222 !important;
    border-color: var(--border) !important;
    border-radius: 1mm !important;
    border-width: 2px !important;
    border-style: solid !important;
}

div.legend,
div[id=intro],
.bg-light {
    background-color: var(--foreground) !important;
    border-color: var(--border) !important;
    border-radius: 1mm !important;
    border-width: 1px !important;
    border-style: solid !important;
}

aside {
    color: var(--textColor) !important;
    background-color: var(--background) !important;
    border-color: var(--border) !important;
    border-radius: 1mm !important;
}

div.p-2 {
    color: var(--textColor) !important;
    background-color: transparent !important;
    border-color: var(--border) !important;
    border-radius: 1mm !important;
}

div.p-2.forumpost {
    background-color: transparent !important;
}

div[role] {
    color: var(--textColor) !important;
    background-color: var(--foreground) !important;
    border-color: var(--border) !important;
    border-radius: 1mm !important;
}

section#region-main {
    color: var(--textColor) !important;
    background-color: var(--foreground) !important;
    border-color: var(--border) !important;
    border-radius: 1mm !important;
}

div#region-main-box {
    background-color: var(--background) !important;
}

div[role=textbox] {
    border-radius: 0mm !important;
}

section {
    color: var(--textColor) !important;
    background-color: transparent !important;
    border-color: var(--border) !important;
    border-radius: 1mm !important;
    background-clip: padding-box !important;
}

.section.current {
    background: #252525 !important;
    border-top: #0098a1 solid 2px !important;
}

.section.current * {
    background: transparent !important;
}

/* remove any color */
.dropdown-menu:first-child {
    background-color: var(--foreground) !important;
}

/* set own hover-color */
.dropdown-menu > a:hover,
.dropdown-menu *:hover *,
.list-group-item:hover *,
.list-group-item *:hover * {
    background-color: var(--backgroundButton) !important;
    border-color: #0f0 !important;
}

li,
ul,
td,
section-2,
nav,
.bg-white,
a.list-group-item,
footer,
h3,
h3.span,
.sectionname,
main,
section.node_category/*, div.d-flex/**/
{
    color: var(--textColor) !important;
    background-color: var(--foreground) !important;
    border-color: var(--border) !important;
}

p {
    background: transparent !important;
}

/*header,
header *, /**/
.bg-transparent {
    background-color: var(--background) !important;
}

nav.bg-white,
nav.bg-white * {
    background-color: #0c0c0c !important;
}

nav.bg-white {
    border-bottom: 1px solid var(--border) !important;
}

button.btn-secondary,
button.nav-link {
    color: var(--textColor) !important;
    background-color: var(--backgroundButton) !important;
    border-color: var(--border) !important;
    border-radius: 2mm !important;
}

div.message {
    color: var(--textColor) !important;
    background-color: var(--backgroundButton) !important;
    border-color: var(--border) !important;
    border-radius: 1mm !important;
}

div.message.received,
div.message.received * {
    color: var(--textColor) !important;
    background-color: #111 !important;
}

div.message.send,
div.message.send * {
    color: var(--textColor) !important;
    background-color: #333 !important;
}

i.icon {
    background: transparent !important;
}

div.count-container {
    background: #f00 !important;
}

i#back-to-top {
    background: var(--link) !important;
    z-index: 10;
}

div.alert,
div.alert-danger {
    color: var(--textColor) !important;
    background-color: #f224 !important;
    border-color: #f224 !important;
    border-radius: 1mm !important;
    border-width: 1px !important;
    border-style: solid !important;
}

div.alert button,
div.alert-danger button {
    color: var(--textColor) !important;
    background-color: transparent !important;
    opacity: 0.9 !important;
}

.unread {
    background: transparent !important;
}

button#loginbtn {
    background-color: var(--backgroundButton) !important;
    border-color: var(--border) !important;
    border-radius: 2mm !important;
}

input[type="checkbox"] {
    display: none;
}

input[type="checkbox"] + label::before {
    width: 16px !important;
    height: 16px !important;
    background-color: var(--backgroundButton) !important;
    border: 2px solid var(--border);
    border-radius: 5px !important;
    display: block !important;
    content: "";
    float: left;
    margin-right: 3px;
}

input[type="checkbox"]:checked + label::before {
    box-shadow: inset 0px 0px 0px 3px var(--foreground);
    background-color: #eee !important;
}

div.atto_group {
    background: var(--backgroundButton) !important;
    border-color: var(--border) !important;
    border-radius: 0mm !important;
}

div.atto_group button {
    background: var(--backgroundButton) !important;
    border-color: var(--border) !important;
    border-radius: 0mm !important;
}

button.atto_collapse_button.highlight {
    background-image: radial-gradient(ellipse at center, #000 20%, var(--backgroundButton) 100%) !important;
}

div[id="id_description_editor_atto_equation_preview"] {
    background-color: var(--backgroundButton) !important;
    border: 1px solid var(--border) !important;
    border-radius: 2mm !important;
}

div.moodle-dialogue-wrap *,
div.moodle-dialogue-content * {
    color: var(--textColor) !important;
}

blockquote pre {
    color: var(--textColor) !important;
}

/* fuck no, not black text! get that outta here*/
*[style*="color: #000000"],
*[style*="color: #000"] {
    color: #fff !important;
}

td.submissionstatussubmitted,
td.earlysubmission {
    background-color: #053305 !important;
}

.calendar_event_course,
.calendar_event_course h3 {
    background-color: var(--calendarEventCourse) !important;
    border-color: var(--calendarEventCourse) !important;
}

.calendar_event_site,
.calendar_event_site h3 {
    background-color: var(--calendarEventSite) !important;
    border-color: var(--calendarEventSite) !important;
}

.calendar_event_category,
.calendar_event_category h3 {
    background-color: var(--calendarEventCategory) !important;
    border-color: var(--calendarEventCategory) !important;
}

.calendar_event_group,
.calendar_event_group h3 {
    background-color: var(--calendarEventGroup) !important;
    border-color: var(--calendarEventGroup) !important;
}

.calendar_event_user,
.calendar_event_user h3 {
    background-color: var(--calendarEventUser) !important;
    border-color: var(--calendarEventUser) !important;
}

td.day.today {
    background-color: #050 !important;
}

div[id="modal-content"] {
    background-color: var(--foreground) !important;
    border-color: var(--border-color) !important;
}

/* untested */
div.editor_atto_toolbar button .icon {
    color: black !important;
}

/* untested */
.forumng-post.forumng-read {
    color: white
}

mark {
    background-color: #332b00 !important;
    color: var(--textColor) !important;
    border: 1px #bfa300 solid;
    border-radius: 3px;
    padding: 2px;
}

hr {
    border-color: #0098a1 !important;
}

.dimmed *,
.dimmed_text * {
    color: #bbb !important;
}

pre {
    color: #c0d9f0 !important;
}

/* invert color of default profile picture */
img[src="https://lms.beuth-hochschule.de/theme/image.php/boost_campus/core/1590829203/u/f1"],
img[src="https://lms.beuth-hochschule.de/theme/image.php/boost_campus/core/1590829203/u/f2"] {
    -webkit-filter: invert(.85);
    filter: invert(.85);
}
}
