css:
@-moz-document regexp("https://.*myshopify.com/.*") {
    iframe#article-content_ifr,
    iframe#product-description_ifr {
        height: 70vh !important;
    }
    .ui-layout,
    ._1fbVL,
    .ui-title-bar-container,
    ui-page-actions {
        max-width: 200rem;
    }
    .image-picker__placeholder .aspect-ratio__content,
    .image-picker__preview-image .aspect-ratio__content,
    .image-picker__drop-preview .aspect-ratio__content,
    .image-picker__loading .aspect-ratio__content {
        width: calc(100% - 2px);
    }
    .sfe-next .theme-setting__input--textarea,
    .sfe-next .theme-setting__input--html {
        height: 250px;
    }
    #theme-editor > main > form {
        width: 400px;
    }
    @media screen and (min-width: 1620px) {
        #theme-editor > main > form {
            width: 500px;
        }
    }
    .js-image-picker-container .image-picker__placeholder,
    .js-image-picker-container .image-picker__preview-image {
        height: 210px;
    }
    .js-image-picker-container .image-picker__placeholder .js-image-picker-preview-image,
    .js-image-picker-container .image-picker__preview-image .js-image-picker-preview-image {
        height: 100%;
        padding: 0;
    }
    @media screen and (min-width: 600px){
        .te-sidebar--is-closed {
            margin-left: -400px;
        }
    }
    @media screen and (min-width: 1620px) {
        .te-sidebar--is-closed {
            margin-left: -500px;
        }
    }
}
