@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userstyles.org")


{


/******************* BODY ********************/


* {
background-color: #262626 !important;
border: none !important;
color: #8a8a8a !important;
background-image: none !important;
}


/******************* TEXT ********************/


a:link, a:active, a:visited {
color: #9a9a9a !important;
}

a:hover {
color: #000000 !important;
}


/****************** IMAGES *******************/


img[src*="/images/star_0.png"], img[src*="https://m.kallout.com/abc/source.php?id=stylish&img=2"] {
width: 0px !important;
background: none !important;
}

img[src*="/images/star_1.png"] {
width: 0px !important;
height: 14px !important;
background:url(data:image/png;base64,) no-repeat !important;
padding-left: 15px !important;
}

img[src*="/images/star_05.png"] {
width: 0px !important;
height: 14px !important;
background:url(data:image/png;base64,) no-repeat !important;
padding-left: 15px !important;
}

#donate.paypal-user-donate input[type="image"] {
width: 0px !important;
height: 21px !important;
background: url(data:image/png;base64,) no-repeat !important;
padding-left: 74px !important;
}

#donate input[type="image"] {
width: 0px !important;
height: 23px !important;
background: url(data:image/png;base64,) no-repeat !important;
padding-left: 110px !important;
}


}
