css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("friendfeed.com") {
	*::-moz-selection{
	background:#333 !important;
	color:#fff !important;
}

body {background: url(http://artstil1.googlepages.com/ffbggrad.png) 
repeat-x !important;}
.box .box-bar, .box .box-bar .box-corner 
{
background-image: url(http://artstil1.googlepages.com/ffbgort.png) 
!important;
}
.l_profile .picture {border: none !important;}
.box .box-bar, .box .box-bar .box-corner 
{
background-image: 
url(http://artstil1.googlepages.com/ffbgort.png?v=05a7);
}
.subbarcontainer {
background-image: url(http://artstil1.googlepages.com/ffbgort.png) 
}
.box .box-bar, .box .box-bar.friends, .box .box-bar.groups, .box 
.box-bar.search 
{
background-image: url(http://artstil1.googlepages.com/ffbgort.png);
background-repeat: repeat-x;
background-attachment: scroll;
background-position: center top;
border-top-width: 1px;
border-bottom-width: 1px;
border-top-style: solid;
border-bottom-style: solid;
border-top-color: #ccc;
border-bottom-color: #000000;
background-color: #FFFFFF;
}
.box .box-body 
{
background-color: #eeeeee;
background-image: none;
background-repeat: repeat;
background-attachment: scroll;
background-position: 0% 0%;
border-top: 1px #fff solid !important;
border-left: 1px #eee solid !important;
border-right: 1px #eee solid !important;
padding-top: 15px;
padding-left: 25px;
padding-right: 25px;
}

*{
font-family:"Tahoma","Arial","Serif" !important;
color:#333 !important;
font-size: 12px !important;
}
a,a:link,a:visited{
text-decoration:none !important;
color: #ff2a00 !important;
}
a:hover{
color:#111 !important;
border-bottom:1px dotted #333333 !important;
}
a:active{
text-decoration:none !important;
color:#f4a460 !important;
}
.owner{
background-color:#FFE1CE !important; /*Highlight your own 
comments*/
border: 1px #CCCCCC dotted;
}
#sidebar .box ul li a
{
font-size: 12px !important;
color: #000000 !important;
border-bottom: 1px #CCCCCC dotted;
line-height: 20px !important;
}
.box .box-body
{
font-size: 12px !important;
color: #333 !important;
border-bottom: 1px #CCCCCC dotted;
line-height: 20px !important;
}
.box .box-bar, .box .box-bar .box-corner
{
background-image: url(http://artstil1.googlepages.com/ffbgort.png) 

!important;
}
.box-bar, .groups, .home, .friends {border-color:#333!important; 
background-color:#000000!important}

#sidebar {margin-top: 43px !important;}
#feed .entry {background: 
url(http://artstil1.googlepages.com/fffeedbg.png) -2px -2px no-repeat 
!important; border: 1px #CCCCCC dotted !important; margin-bottom: 15px 
!important; padding: 10px !important;}

.logo {background: 
url(http://artstil1.googlepages.com/ffsp.png) no-repeat 
!important;  width: 182px; height: 56px;}
.logo a img {display: none;}

.logo a {display: block; width: 180px; height: 55px; background: none;}
.searchbox {border: none; background: #FFF !important; height: 18px;}
.main ul li a, .section ul li a { font-size: 11px !important; }
}
.sharebox, .entry, .pager.top 
{
padding-bottom: 10px !important;
border-bottom-width: 0px !important;
border-bottom-style: solid;
border-bottom-color: #eeeeee;
}
.box .box-bar .box-bar-text, .box .box-bar .box-bar-text a  
{
color: #FFFFFF !important;
}
#header input
{
font-family: "Lucida Grande",Verdana,Arial,"Bitstream Vera 
Sans",sans-serif !important;
padding-top: 3px !important;
padding-right: 10px !important;
padding-bottom: 3px !important;
padding-left: 10px !important;
font-size: 12px !important;
border-top-width: 1px !important;
border-bottom-width: 1px !important;
border-top-style: solid !important;
border-bottom-style: solid !important;
cursor: pointer !important;
text-decoration: none !important;
margin-top: -3px !important;
background-color: #f2f2f2 !important;
background-image: 
url(http://artstil1.googlepages.com/bggrad.png)  !important;
background-repeat: repeat-x !important;
background-attachment: scroll !important;
background-position: left top !important;
}
.friend{background-color:#FFF1CE !important;color:#111 !important}
