css:
@-moz-document domain("terraria.gamepedia.com") {
html body {
    background: #000;
}
.mw-body, div#content, #box-wikiheader > div, .infocard.terraria, .infocard.terraria .main-heading .hgroup, table, .infocard .main-heading .hgroup {
    background: #111;
}
.infocard, .infocard .main-heading .hgroup > .main, div#footer ul li, h1, h2, h3, h4, h5, h6, div#content, .tocnumber, table, .note-text {
    color: #FFF;
}
div.main-article {
    color: #AAA;
}
#box-wikiheader {
    background: #444;
    border: 1px #FFF solid;
    color: #FFF;
}
#toc, .toc, .infobox, .notice-banner > div, div.thumbinner, .navbox > .header, .catlinks, div.crafts .wrap, .infocard {
    background: #222;
    border: 1px #AAA solid;
    color: #FFF;
}
code {
    background: #000;
    color: #FFF;
    border: 1px #BBB ridge;
}
.infobox .title, .terraria th, div.crafts th, .infobox .section.ids {
    background: #444;
    color: #FFF;
}
.item-link .id, #box-wikiheader .related-info {
    background: #222;
    color: #FFF;
}
.terraria {
    border: 1px #FFF solid;
}
table.lined td, .infocard.terraria {
    border-color: #FFF;
}
#box-wikiheader-toggle-link span:first-child::before {
    border-right-color: #FFF;
    border-bottom-color: #FFF;
}
a, .toctogglelabel {
    color: #FB5;
}
a:visited {
    color: #D8F;
}
a.new {
    color: #FEB;
    background-color: #035;
    border: 1px #FFF8 dotted;
}
a.new:visited {
    color: #FCF;
    background-color: #035;
    border: 1px #FFFB dotted;
}
.mw-parser-output a.external, #latest-version a.external {
    color: #F88;
}
.mw-parser-output a.external:visited, #latest-version a.external:visited {
    color: #8BF
}
.infocard.terraria .main-heading, .infocard .main-heading {
    background-color: #444;
    border-right-color: #444;
}
#mw-panel .portal, .infocard .box, .infocard > .card .content {
    background-color: #444;
}
div#mw-panel div.portal div.body ul li a, #mw-panel.collapsible-nav .portal h3 a, #mw-panel.collapsible-nav .portal h3 a, #mw-panel.collapsible-nav .portal h3 a, #mw-panel.collapsible-nav .portal h3 a:visited, #mw-panel.collapsible-nav .portal.collapsed h3 a, #mw-panel.collapsible-nav .portal.collapsed h3 a {
    color: #0F0A;
}
div#mw-panel div.portal div.body ul li a:visited, #mw-panel.collapsible-nav .portal h3 a, #mw-panel.collapsible-nav .portal h3 a:visited, #mw-panel.collapsible-nav .portal h3 a, #mw-panel.collapsible-nav .portal h3 a:visited, #mw-panel.collapsible-nav .portal.collapsed h3 a, #mw-panel.collapsible-nav .portal.collapsed h3 a:visited {
    color: #0FFA;
}
div.vectorTabs li a, div.vectorTabs li a:hover {
    color: #FFF !important;
}
#footer {
    background: #222;
}
#middlemrec_terraria_gamepedia {
    display: none !important;
}
}
