css:
@-moz-document regexp("https://databricks.*"), regexp("https://.*databricks.*") {
.results-and-comments{
    font-family:'JetBrains Mono';
}

.dataset-info-view{
    background-color: rgba(30, 30, 30);
    color: #DADADA!important;
}

.dataset-info-view .field-name{
    color: #569cd6!important;
}

.dataset-info-view .tree-toggler .spacing-left {
    color: #DADADA!important;
}

.dataset-info-view .tree-toggler .fa {
    color:#B5CEA8!important;
}

.command-buttons .fa {
    color:#B5CEA8!important;
}

.reservoir-table-info p {
    color: #569cd6!important;
}

.reservoir-table-info .table-details-value {
    color: #DADADA!important;
}



div.dataset-info-view div.indent, div.table-view div.indent {
    padding-left: 35px;
    border-left: 1px solid #808080;
}
    

.new-notebook.command.mainCommand .command-text {
    margin: -2px 0 0 -7px;
    padding: 0;
    border-radius: 0px;
    background-color: rgba(30, 30, 30);
    /*background-color: black;*/
    color: black;
}

.new-notebook.command.command-active, .new-notebook.command.command-active:hover {
    box-shadow: none;
}

.new-notebook.command.mainCommand:hover {
    box-shadow: none;
}

.command-result-stats {
    font-family:'JetBrains Mono';
}

.command-result-stats span {    
    font-size: 8px;
}

.command-result-stats span:nth-child(2) {
    font-weight: bold;
    font-size: 13px;
    color: black;
}

.command-result-stats span:nth-child(4) {
    font-weight: bold;
    font-size: 13px;
}

/* Visual Studio code theme */

.CodeMirror {
    font-family:'JetBrains Mono';
    color: #DADADA!important;
}

.cm-s-eclipse-focus .CodeMirror-matchingbracket {
    color: #DADADA!important;
    background-color: #0E4583;
}

.cm-string {
    color: #D69D85!important;
}

.cm-variable {
    color: #9CDCFE!important;
}

.cm-variable-2 {
    color: #9CECFE!important;
}

.cm-keyword {
    color: #569CD6!important;
}

.cm-def {
    color: #DCDCAA!important;
}

/* annotations i.e. @udf */
.cm-meta {
    color: #DCDCAA!important;
}

.cm-operator {
    color: #DADADA!important;
}

.cm-property {
    color: #DCDCAA!important;
}

.cm-number {
    color:#B5CEA8!important;
}

.cm-comment {
    color:#57A64A!important;
}

.cm-builtin {
    color:#D8A0DF!important;
}

.CodeMirror-code {
    color: #DADADA!important;
}

.CodeMirror-cursor {
    border-left:4px solid green;
}

.CodeMirror-gutter {
    background-color: rgb(30,30,30);
}

.CodeMirror-linenumber {
    color: #2B91AF;
}

.submit-hint {
    display: none;
}
}
