css:
@-moz-document domain(forums.terraria.org) {
div {
    color: white !important;
}

.p-pageWrapper {
    background: black !important;
}
.grassBar {
   display: none;
}
.p-nav, .p-sectionLinks-inner, div.p-body-header, div.p-title, .notice-content, .block-container, .p-breadcrumbs, .porta-article-item .block-header, .block-header , .pageNav-jump{
    /* from original site */
    border-top-style: none;
    border-right-color: rgba(0,0,0,0) !important;
    border-bottom-color: rgba(0,0,0,0) !important;
    border-left-color: rgba(0,0,0,0) !important;
}
.p-nav, .p-breadcrumbs, .block-footer, .block-header, .block-tabHeader {
    background: #151515 !important;
}
.p-sectionLinks-inner, .porta-article-item .block-header, .notice.notice--primary {
    background: #101010 !important;
}
div.p-body-header, .shareButtons.shareButtons--iconic, .menu-footer.menu-footer--split, .menu-row.menu-row--alt, form.menu-footer, .attachment, .avatarHolderReg,.fr-popup, .message-responseRow, .pageNav-jump.pageNav-jump--next, .pageNav-page.pageNav-page--current, .pageNav-page.pageNav-page--current + .pageNav-page, .pageNav-page:last-child, .notice.notice--accent, .tabs--standalone, .block-filterBar, .pageNavSimple-el.pageNavSimple-el--last, .pageNavSimple-el.pageNavSimple-el--next, .pageNav-page{
    background: #151515 !important;
    color: white !important;
}
div.p-title, .fr-command.fr-btn + .fr-dropdown-menu .fr-dropdown-wrapper {
    background: #101010 !important;
}
.notice-content, .formSubmitRow-bar, h4.menu-tabHeader.tabs {
    background: #101010 !important;
}
.block-container, .menu--structural.menu--right .menu-content, .menu-header, .block--messages .message, .block--messages .block-row {
    background: #202020 !important;
}
/* set theme text */
.fa-paint-brush:after {
    content: ' ULTRADARK';
}
img[src*="/assets/slime.gif"] {
  position: relative;
  overflow: hidden;
  width: 128px;
  height: 104px;
  padding: 128px 104px 0 0;
    background: transparent url('https://i.imgur.com/ZsS5n5s.gif') no-repeat;
} /* make the slimes have party hats :P */

}
