css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {

body, input, textarea, select, option, button, th, td {
	font-family: Tahoma !important;
	font-size: 12px !important;
}

a {
	text-decoration: none !important;
	color: #0097e5 !important;
}

a:hover {
	color: #d29e01 !important;
}

.header .stat a {
	font: 12px Tahoma !important;
}

#container {
	background-color: #d1d1d1 !important;
}

.body h1 {
	font: bold 23px Arial !important;
}

.body .l_editprofile {
	font: 11px Tahoma !important;
}

.subscribebar a, .subscribebar a:hover, .box-bar-text a, .box-bar-text a:hover {
	color: #fff !important;
}

.box-body {
	padding-top: 30px !important;
}

.sharebox {
	border: none !important;
}

.entry {
	background-color: #fefefe !important;
	border: 2px solid #fbfbfb !important;
	padding: 10px !important;
	-moz-border-radius: 5px !important;
	-khtml-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
	margin-bottom: 35px !important;
	opacity: 0.60 !important;
}

.entry:hover {
	opacity: 100 !important;
}

.entry .title .name a.l_profile {
	font: 12px Tahoma !important;
}

.entry .body .title .text {
	font: bold 15px Arial; !important;
	color: #777 !important;
	margin-bottom: 15px !important;
}

.body .info {
	font: 11px Tahoma !important;
}

.info a .date {
	color: #666 !important;
}

.entry.direct .info {
background-image:url(http://i.friendfeed.com/c326c3df6d1d19ebdaaa5e7583dc5d177b255b16) !important;
}

.body .likes {
	margin-right: 0px !important;
	font-size: 12px !important;
}

#feed .comments {
	font: 12px Tahoma !important;
    background-color: #fafafa !important;
	padding: 5px !important;
	-moz-border-radius: 5px !important;
	-khtml-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
	margin: 10px 5px !important;
}

.entry .comment {
	font: 12px Tahoma !important;
	line-height: 180% !important;
}

.friend {
	background-color: #f6f6f6 !important;
	border: 1px solid #eee!important;
	-moz-border-radius: 5px !important;
	-khtml-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
    padding: 2px !important;
}

.owner {
	background-color: #ffffd8 !important;
	border: 1px solid #eee777 !important;
	-moz-border-radius: 5px !important;
	-khtml-border-radius: 5px !important;
	-webkit-border-radius: 5px !important;
	border-radius: 5px !important;
}

.comment .quote:before {
	content:attr(title) !important;
	display: none !important;
}

.comment .quote:hover{
	z-index:10 !important;
	position:relative !important
}

.entry .comment.deleted, .entry .comment.friend, .entry .comment.owner, .entry .commentform {
	background-image: url("http://i.friendfeed.com/20de5ec662792c2d18fc5a3447b036023e3ba571") !important
}

#feed .comments .content {
	overflow: hidden !important;
}

div.box li a[href*=direct].unread {
	background: url(http://i.friendfeed.com/0f1f47bfb33fc619768988e9805a99d8cea08307) no-repeat left !important;
	padding-left: 20px !important;
	color: #ff5418 !important;
}

/**** Services Icon back! ****/
a.service {
	font: bold 10px Tahoma !important;
}
a.service[href*=twitter\.com] {color: #00DEEE !important;}
a.service[href*=twitter\.com]:before {content: url('http://friendfeed.com/static/images/icons/twitter.png') " " !important}
