css:
@-moz-document domain("www.freecodecamp.org") {
:root {
 	--mainBg: #132245;
	--notification-text: #f2c9da;
	--notificationBg: #c83763;
}

html, .card-content, .map:first-of-type {
	background-color: var(--mainBg) !important; 
}

.site-nav .nav li a, .d-header .d-header-icons {
	font-family: cursive !important;
}

/* Menu Panel */
.menu-panel {
	background-color: var(--mainBg) !important;
}

.user-menu .quick-access-panel li {
	background-color: rgba(17, 73, 141, 0.5) !important;	
}

.user-menu .quick-access-panel .read {
	background-color: var(--mainBg) !important;	
}

.user-menu .quick-access-panel li:hover {
	background-color: rgba(17, 73, 141, 1) !important;
}

/* Links (Subforum, Latest, etc.) */ 

.nav-pills>li>a:hover, #create-topic:hover, .navigation-container .category-breadcrumb .combobox .single-select-header:hover, #reply-control .save-or-cancel button:hover, .timeline-container .topic-timeline .timeline-footer-controls button:hover, .btn-default:hover, .btn-primary:hover  {
	background-color: #2994ff !important;
	border-color: #2994ff !important;
	
}

.nav-pills>li>a, #create-topic, .navigation-container .category-breadcrumb .combobox .single-select-header, #reply-control .save-or-cancel button, .timeline-container .topic-timeline .timeline-footer-controls button, .btn-default, .btn-primary {
	border: 3px solid #005fc1 !important;
	background-color: #005fc1 !important;
	color: #daeaf6 !important;
	font-family: cursive;
}

/* Reply Area */
#reply-control .reply-area {
	background-color: var(--mainBg) !important;	
}

#reply-control #reply-title, .select-kit.combo-box .select-kit-header, .d-editor-textarea-wrapper {
	background-color : #162750 !important;  
}

.reply-area {
	font-family: cursive;	
}

/* Profile Menu */
.details, .user-content, section.user-content .user-stream .user-stream-item  {
	background-color: var(--mainBg) !important;
}

.discourse-touch .topic-body .actions .fade-out:hover,  nav.post-controls .share:hover,  nav.post-controls .show-more-actions:hover {
	background-color: #2994ff !important;
	color: #daeaf6 !important;
}

.notification-history .ember-view .item{
	background-color: var(--mainBg) !important;
}

.notification-history .ember-view .unread {
	background-color: rgba(17, 73, 141, 0.5) !important;
}

.d-header {
 	background-color: #1a284b !important;
}

.d-header-icons .unread-notifications, .badge-notification.new-posts, .badge-notification.unread-posts {
 	background-color: var(--notificationBg) !Important;
	color: var(--notification-text) !important;
}

p>code, li>code, pre>code {
	background-color : #374667 !important;  
	font-family: cursive;
}

.category-list .category .badge-notification, .category-list .topics .badge-notification {
	background: transparent !important;
    color: #767684 !important;
}

.badge-notification.new-topic::before {
 	color:	var(--notificationBg) !important;
}
}
