css:
@-moz-document domain("ororo.tv") {
@media only screen and (min-width: 600px) {
    #content{
		margin:2% 0px 0px !important;
        padding:0px !important;
	}
    [class*="__left-side"]{
		width:100%;
		padding-right:5% !important;
    }
    [class*="__left-side"] img{
		min-width:50%;
		float:left;
		margin-right:50px;
	}
    
    /** Right site */
    [class*="__right-side"]{
		width:100%;
	}
    [class*="__title"]{
		font-size:50px;
		padding:0px 5%;
	}
    [class*="__description"]{
		padding:0px 5%;
	}
    
    /** Films styles */
    .movie-content__right-side > a {
		margin: 0px 5% 5%;
		width:90%;
		display:block;
	}
    .movie-content__right-side > a div{
		height: 100px !important;
		width:100%;
		
	}
    
    /** Series styles */
    .media-controls{
		width:100%;
		padding-left:55%;
	}
    .media-controls div{
		width:100%;
		position:relative;
		margin:5% 0px !important;
	}
    .media-controls div > a{
		display:block;
		min-height:70px;
		width:400px !important;
        white-space: nowrap !important;
        overflow: hidden !important;
        text-overflow: ellipsis !important;
	}
    .show-content__season-list li{
		padding: 50px !important;
	}
    .show-content__season-list li a{
		font-size:30px !important;
		padding:0px !important;
	}
    .episode-box{
		padding:0px 20px !important;
	}
    .episode-box a.show-content__episode-link{
		font-size:40px !important;
	}
    .show-content__episode-num{
		min-width:10%;
	}
    .episode-box .episode-plot,
    .episode-box .episode-plot__text{
		width:100%;
		max-width:100% !important;
	}
    .watched-icon{
		float:right;                                            
		margin-right:5% !important;                                 
	}
    .watched-icon i{
		font-size:40px !important;                                     
	}
    .episode-plot + span,
    .episode-button-box,
    .info-box,
    .subtitle-icons{
		display:none !important;
	}
    
    [class*="__upcoming-title"]{
		padding: 0px 5%;
	}
    [class*="__upcoming-episode"]{
		padding: 0px 5%;
	}    
}
}

@-moz-document domain("ororo.tv") {
@media only screen and (min-width: 600px) {
    /* remove social stuff: */
    #vk_groups, #social {
        display: none !important;
    }
    /** Header */
    .header-wrapper {
    	height: 180px !important;
    	margin-top: -60px;
    }
    header *,
    #content *{
		font-size:25px !important;
		line-height:50px;             
		box-sizing: border-box;
	}
    header .container{
		padding:0px !important;
		margin:0px !important;
		max-width:100% !important;
	}
    header nav{
		width:100%;
		text-align:center;
		position:relative;
	}
    nav .header-nav-logo-wrap{
		position:absolute;
		width:20%;
		top:100px;
		left:0px;
	}
    nav .site-logo{
		background-image: url('https://ororo.tv/assets/logo-7357121603f7afa41b456a871fdbff02dafe08b8cefff7a7e7cb320a57080bdc.svg');
		background-repeat:no-repeat;
		background-size:70%;
		background-position:center;
		width:100%;
		height:50px;
		margin:0px !important;
		padding:0px !important;
	}
    nav .site-logo img{
		display:none;
	}
    /** Menu */
    .menu-left{
		width:100%;
		padding:20px 0px;
	}
    .menu-left ul{
		width:100%;
	}
    .menu-left li{
		width:33%;
	}
    .menu-left a{
		font-size:15px;
	}
    /** Menu right */
    .menu-right{
		width:80%
	}
    .menu-right ul{
		width:100%;
	}
    .menu-right li,
    .menu-right li a{
		height:auto !important;
	}
    .menu-right li:nth-child(1),
    .menu-right li:nth-child(2){
		min-width:15%;
	}
    .menu-right li:nth-child(3),
    .menu-right li:nth-child(4){
		min-width:20%;
	}
    .menu-right li:nth-child(3) a .name{
		display:none !important;
	}
    .menu-right li:nth-child(5){
		min-width:30%;
     	margin-top:-18px;
	}
    .menu-right li:nth-child(5) > a{
		width:100%;
	}
    .menu-right .hot-icon{
		margin: 10px !important;                     
	}
    .menu-right a svg{
		width:30px !important;
		height:30px !important;
	}
    .icon-triangle{
		margin-top:-12px;
		width:40px !important;
		height:40px !important;
	}    
    .dropmodule-notifications .submenu-wrap{
        width: calc(100vw + 15px) !important;
        top: 62px !important;
        left: 0%;
        transform: translate(-21%, 0%);
	}
    .dropmodule-notifications .icon-triangle{
		left:25.5% !important;
	}
    .dropmodule-favorites .submenu-wrap{
        width: calc(100vw + 15px) !important;
        top: 62px !important;
        left: 0%;
        transform: translate(-32%, 0%);
		height:100% !important;
	}
    .dropmodule-favorites .icon-triangle{
		left:35.5% !important;
	}
    .dropmodule-favorites a .badge{
		padding: 10px !important;
    	height: 40px !important;
    	min-width: 40px !important;
    	max-width: 110px !important;
		left: 20px !important;
		top: -15px !important;
    	border-radius: 25% !important;
    }    
    .submenu-wrap .movie-icon{
		width:20% !important;
		margin:0px 10px;
	}
    
    .favorites-list{
		background-color:#FFF;
	}
    .favorites-list > div{
		padding:3%;                        
	}
    .movie-icon-primary, .movie-icon-remove {
		width:100% !important;
	}
    .movie-info p{
		height:auto !important;
		line-height:35px !important;
		text-align:left;
	}
    .search-box{
        width: 100vw!important;
        left: 0% !important;
        transform: translate(-76%, 0%);
	}
    .search-box .search-form{
		height:80px !important;
	}
    .search-form .icon-search{
		width:40px !important;
		height:40px !important;
	}
	.search-form input[type=text]{
		font-size:35px !important;
	}
    .search-form .search-box-close{
		width:15% !important;
	}
}
}

@-moz-document domain("ororo.tv") {
@media only screen and (min-width: 600px) {
/** Seasons */
body {
	counter-reset: seassons; 
}

.show-content__season-list{
    display:block;
    width:90% !important;
	margin: 0px 5% 25px 5% !important;    
}
.show-content__season-list li{
    position:relative !important;
    width:auto !important;
    padding:0px !important;
    margin:0px !important;
    border:1px solid #dcddde !important;
    border-radius:20% 20% 0px 0px !important;
    line-height: 20px !important;
}
.show-content__season-list li a{
    box-sizing:inherit;
    display: inline-block;
    width:80px !important;
	white-space: nowrap !important;
    overflow: hidden !important;
    padding:15px !important;
}
.show-content__season-list li a:before{
  counter-increment: seassons;   
  content: "S" counter(seassons);
  padding:5px 50px 0px 15%;
    
}
}
}
