@-moz-document domain("weibo.com") {
  :root {
    --c-title: #212121;
    --c-text: #666;
    --c-border: #ccc;
    --c-background: #fff;
    --c-hover: rgba(230,230,230,.3);
    --c-accent: #00a1d6;
    --c-white: #fff;
    --c-bg-header: rgba(0,0,0,.3);
    --c-scroll: #7c7d7f;
    --bg-image: none;
    --c-bg-mask: rgba(255,255,255,0);
  }
  @media(prefers-color-scheme: dark) {
    :root{
      --c-title: #e2e2e2;
      --c-text: #bababa;
      --c-border: #4e4e4e;
      --c-background: #25272a;
      --c-hover: rgba(20,20,20,.3);
    }
  }
  html {
    height: auto;
    min-height: 100vh;
    background-color: var(--c-background);
    background-image: var(--bg-image, none);
    background-attachment: fixed;
    background-position: center;
    background-size: cover;
    background-repeat: no-repeat;
  }
  body {
    height: auto;
    min-height: 100vh;
    overflow-x: hidden;
    background-color: var(--c-bg-mask) !important;
    background-image: none !important;
  }
  a,
  a div,
  a p,
  a span,
  h1,
  h2,
  h3,
  h4,
  h5,
  h6,
  input,
  textarea,
  em {
    color: var(--c-title) !important;
  }
  body,
  p,
  span,
  li,
  label,
  div {
    color: var(--c-text) !important;
  }
  a:hover,
  a:hover div,
  a:hover p,
  a:hover span {
    color: var(--c-accent) !important;
  }
  hr {
    border-color: var(--c-border) !important;
    background-color: var(--c-border) !important;
  }
  select {
    border-color: var(--c-border) !important;
    border-radius: 4px;
    background-color: var(--c-hover) !important;
    color: var(--c-title) !important;
  }
  :focus {
    outline-color: var(--c-accent);
  }
  ::-webkit-input-placeholder {
    color: #d2d2d2;
  }
  :-moz-placeholder {
    color: #d2d2d2;
  }
  ::-moz-placeholder {
    color: #d2d2d2;
  }
  :-ms-input-placeholder {
    color: #d2d2d2;
  }
  ::-webkit-scrollbar {
    max-width: 6px;
    max-height: 12px;
  }
