css:
@-moz-document domain('youtube.com') {

ytd-live-chat-frame {
    margin: 0;
    position: absolute;
    left: 0;
    top: 50px;
    width: 450px;
    padding: 0;
    height: calc(100vh - 100px) !important;
    opacity: 0.06;
    border: none;
    z-index:1;
  }

  ytd-live-chat-frame:hover {
    opacity: .95;
    transition: .5s;
  }

  div#chat {
    position: initial;
  }

  #show-hide-button {
    margin-top: -10px;
    filter: blur(1px);
  }

  #show-hide-button:hover {
    filter: none;
    transition: .5s;
  }
  
  #item-scroller {
  scrollbar-color: #494242 #181818;
  scrollbar-width: thin;
  }

  yt-live-chat-header-renderer {
    filter: blur(2px);
  }

  yt-live-chat-header-renderer:hover {
    filter: none;
    transition: .5s;
  }
}
