css:
@-moz-document domain("spotify.com") {
/* убираю текст с элементов меню слева, делаю их в строчку */
.navBar-item--with-icon-left,
.navBar-item--with-icon-right {
    font-size: 0;
}

.navBar__entry-points {
    display: flex;
    align-items: center;
    justify-content: space-between;
}

/* центрирую заголовок меню с плейлистами*/
.Rootlist__playlists-header {
    text-align: center
}

/* делаю отступы от краёв поменьше */
.navBar {
    padding-top: 8px;
}

.navBar-header .logo {
    text-align: center;
    padding-left: 0 !important;
    padding-bottom: 0;
}

.Rootlist {
    --left-sidebar-item-height: 30px;
    margin-top: 8px;
}

.RootlistItem {
    padding-left: 0;
    padding-right: 8px;
    position: relative;
}

.ce8ac4aabb35a48dc93a67cf4fedd99c-scss,
.fcdf941c8ffa7d0878af0a4f04aa05bb-scss {
    padding: 10px 8px;
    background: linear-gradient(90deg, rgba(40, 40, 40, 1) 0%, rgba(80, 80, 80, 1) 100%);
}

.f92d0df6ae4da826d7d532fbf608d469-scss {
    text-align: center
}

.b5b01e376bfe3156c865cba3909f11c3-scss {
    padding: 0px 24px 0px 8px !important;
}



/* убираем круги и возвращаем квадраты */
._26d9e31a05dd5fba3afe1b281ae2cf9e-scss ._66febc2caff37c822a831232b6e73171-scss,
._34098cfd13d48e2910679f35aea2c377-scss,
._71b2b74d199d96b907da0c433c3c8da6-scss,
