._11f5fc88e3dec7bfec55f7f49d581d78-scss {
    border-radius: 0
}

/* убираем нахуй кнопку "установить приложение" */
.NavBarFooter {
    display: none
}

/* а также рекламу */
._3c08e30e0b5018cc15edf990e21d3dc5-scss {
    display: none
}

/* и кнопку "upgrade"  */
.a88f0b25501df313b75b70d458d9fe87-scss {
    display: none
}

/* да и разделитель слева тоже можно */
.Rootlist__divider {
    display: none
}

