css:
@-moz-document regexp("^.*\\.gogoanime\\..*$") {
/* Main layout*/
.anime_name {
    background-color: inherit;
    padding: 11px 0 11px 20px;
}

.anime_name h2 {
    color: white;
    font-size: 2.0em;
    font-weight: lighter !important;
    font-family: Calibri;
}

.content_left {
    width: 100% !important;
}
.content_right {
    display:none;
}

.title_name {
    width: 100%;
}

.main_body {
    margin-bottom: 0px;
    border: none;
}

.croll {
    display: none;
}

.name {
    height: auto !important;
}

.content {
    margin-top: 50px !important;
    min-height: calc(100vh - 57px - 95px - 70px);
}

.img .type {
    color: white;
    background: none !important;
    background-color: #1b1b1b !important;
    width: 25% !important;
    padding: 5px 10px 20px 10px !important;
    height: 0px !important;
    margin: 5px 35% !important;
    border-radius: 30px;
}

.ic-SUB::after {
    font-weight:bold;
    content:"SUB";
}

.ic-DUB::after {
    font-weight:bold;
    content:"DUB";
}

.anime_info_body h1 {
    font-weight: lighter !important;
    font-family: Calibri;
    font-size: 2.0em;
}

.added_series_body.popular ul li > a {
    font-weight: lighter !important;
    font-family: Calibri;
    font-size: 1.2em;
}

.dub.active {
    color: #ffc119 !important;
}

a.dub {
    color: #565656;
}

footer {
    bottom: 0;
    width: 100%;
}

html, body, #wrapper {
    background-color: #1b1b1b !important;
}

.items li {
    margin-bottom: 30px !important;
}

.pagination ul li a, .list_search ul li a {
    color: #565656;
    border-radius: 10px;
}

.pagination ul li a:hover, .pagination ul li.selected a, .list_search ul li a:hover, .list_search ul li.selected a {
    background-color: #ffc119;
    border-radius: 20px;
}

.anime_info_episodes h2 {
    border-bottom: 0px;
}

.last_episodes ul {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    list-style-type: none;
}

.last_episodes ul li {
    width: 90% !important;
}

/* header imges */
.anime_name_img_episode_video, .icongec-anime_video, .headnav_left, .icongec-recent_release, .icongec-added_series, .anime_name_img_comedy {

    display: none !important;

}

.anime_name.anime_movies, .icongec-anime_movies, .anime_name.anime_list, .icongec-anime_list, .icongec-new_series, .anime_name.new_series, .anime_name.anime_info, .icongec-anime_info, .anime_name.episode_video, .anime_name.anime_video, .anime_name.recent_release, .anime_name_img_recent_release, .anime_name.added_series, .anime_name.comedy {

    background-image:none;

}

/* Main layout - nav header */
.headnav_right {
    margin-right: 20px;
}

#search-form .row {
    margin-top: 15px;
    border-radius: 20px !important;
    background-color: #1b1b1b;
    border: 0px;
}


nav.menu_top ul li a {
    font-family: Calibri, Geneva, sans-serif;
}

.btngui {
    margin-right: 5px;
}

.loader {
    right: 140px;
    top: 25px;
    border: 2px solid #141414;
    border-top: 2px solid #ffc119;
}

#header_search_autocomplete_body {
    margin-top: 0px;
    background-color: #1b1b1b ;
    border: 1px solid #ffc119;
}

.user_auth {
    float: right;
    margin-left: 5px;
    margin: 0px 0px 0px 0px;
    position: absolute;
    top: 0;
    left: 0;
}

.user_auth .fix {
    display: none;
}

.user_auth .reg::before {
    color: #ffc119;
    content:"| ";
}

.user_auth ul li {
    padding: 3px;
    background-image: none;
}

.submenu_intro span {
    display: none;
}

.submenu_intro a::after {
    color: #ffc119;
    content:" |";
}

.menu_top_link {
    margin-top: 20px;
    padding: 0px;
}

.headnav {
    padding: 0px 0px 10px 0px !important;
    margin-bottom: 35px;
    background-color: #141414;
    width: 100%;
    position: absolute;
    left: 0;
    top: 0;
}

/* Player */
.anime-info span {
    display:none;
}

.anime-info a {
    margin-left: 5px;
    font-size: 1.3em;
    display: block !important;
}

.anime_video_body_cate {
    padding-top:0;
}

.anime_video_body h1,
.anime_video_note_watch,
.anime_video_body_cate + div, 
.anime_video_body_cate span,
.anime_video_body_cate a{
    display: none;
}

.anime_name.anime_video {
    padding-bottom: 0;
}

.anime_name.anime_video .title_name {
    padding-left: 5px;
}

.download-anime a {
    padding: 50px;
}

.btndownload {
    padding: 0 8px 0 30px;
}

.btndownload, .favorites_book ul li {
    border-radius: 20px;
}

.anime_video_body_watch {
    margin: 0;
}

.anime_video_body {
    padding-top: 0;
}

.anime_muti_link {
    display: block;
}

#episode_page {
    display: none;
}

.link_face {
    display: none;
}

.anime_video_body_episodes_r a,
.anime_video_body_episodes_l a{
    font-size: 1;
    text-transform: uppercase;
}


.tooltip {
    border: none;
    background-image: none;
    background-color: #141414;
    border-radius: 5px;
}

}
