.aft-article h1,
.aft-postcontent h2,
.aft-postcontent h3 {
    color: var(--aft-base__color);
}

.aft-button,
a.aft-button,
a.aft-button:link,
a:link.aft-button:link,
body a.aft-button:link,
a.aft-button:visited,
body a.aft-button:visited,
