}


/* input */

input[type="text"],
input[type="password"],
input[type="email"],
input[type="url"] {
    color: var(--aft-base__color) !important;
    background: var(--aft-input__background-color);
}


/* pager */

.aft-pager>*,
.aft-pager .more {
    background: var(--aft-input__background-color);
}

.aft-pager a:link,
.aft-pager a:visited {
    color: var(--aft-button__color);
}

.aft-pager .active,
.aft-pager a:hover {
    background: var(--aft-input__background-color);
    color: var(--aft-a--hover__color);
}

.aft-postheader,
.aft-postheader h1 {
    color: var(--aft-base__h1__color);
    margin-bottom: 8px;
