css:
/* i really want this to be global */
.chat__message {
    border-bottom: none;
    border-radius: 8px;
    margin: 4px 8px;
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    justify-content: flex-start;
    align-content: center;
    align-items: center;
    padding: 2px;
    position: relative;
}
.chat__message__content-wrapper {
    margin-left: 64px;
    width: 100%;
    background: #14182b;
    border-bottom: none;
    border-radius: 8px;
    padding: 8px 16px;
    position: relative;
}
.chat__message__content-wrapper:after {
content: '';
position: absolute;
border-style: solid;
border-width: 15px 17px 15px 0;
border-color: transparent #14182b;
display: block;
width: 0;
z-index: 1;
margin-top: -15px;
left: -17px;
top: 25%;
}
    
.chat__message__avatar {
    position: absolute;
    left: 0;
    bottom: 10px;
    top: 0;
    width: 44px;
    height: 44px;
    max-height: 44px;
    max-width: 44px;
    overflow: hidden;
    border: 2px solid rgba(255, 255, 255, .075);
}
    
.chat__message__avatar:hover {
    margin: 4px;
    height: 44px;
    width: 44px;
    max-height: 44px;
    max-width: 44px;
    overflow: hidden !important;
    border: 2px solid rgba(255, 255, 255, .075);
    border-radius: 100%;
}
.chat__message__content-wrapper:hover {
    background: #333745;
}
.chat__message__avatar:hover {
    overflow: hidden;
}
.chat__message__avatar img {
    width: 100%;
    height: 100%;
}
.chat__input {
    background: rgba(0, 0, 0, .1);
    color: #f3f3f3;
    width: 100%;
    flex: 1 1 auto;
}
.chat__submit {
    background: #f26177;
    top: 4px;
    right: 8px;
}
.chat-wrapper {
    background: linear-gradient(138deg, #080c1c 0%, #080c1c 100%);   
}
.chat__input-wrapper {
    margin-top: 8px;
    padding: 4px 8px;
    background: linear-gradient(138deg, #252938 0%, rgba(15, 18, 32, 1) 100%);
    border-top: 2px solid rgba(255, 255, 255, .25);
}
.chat__input-wrapper form {
    width: 100%;
}
.chat__message__head {
    margin-top: 4px;
}
.chat__message__user {
    color: #fff;
}
.chat__message__content {
    color: #f3f3f3;
    filter: brightness(300%);
}
